import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login'; 
import { isAuthenticated } from './utils/auth'; 
import Usuario from './pages/Seguridad/usuario';
import CrearUsuario from './pages/Seguridad/crear-usuario';  // Componente para crear usuarios
import Dashboard from './pages/Dashboard';

function ProtectedRoute({ children }) {
    if (!isAuthenticated()) {
        return <Navigate to="/login" />;
    }
    return children;
}

function App() {
    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route path="/login" element={<Login />} />

                {/* Rutas protegidas para Dashboard */}
                <Route path="/dashboard" element={<ProtectedRoute><Dashboard /></ProtectedRoute>}>
                    {/* Rutas dentro del Dashboard */}
                    <Route path="seguridad/usuarios" element={<Usuario />} />
                    {/* Ruta para crear un usuario dentro del Dashboard */}
                    <Route path="seguridad/crear-usuario" element={<CrearUsuario />} />
                </Route>

                {/* Redirigir cualquier ruta no encontrada al login */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
