import React, { useState, useEffect } from 'react';
import { Box, Grid, Typography, Checkbox, FormControlLabel, TextField } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom'; // Para obtener el id del rol
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Función para verificar autenticación

const CrearPermisos = () => {
  const [showAlert, setShowAlert] = useState(null); // Alerta
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el id del rol desde la URL

  const [rolName, setRolName] = useState('');
  const [descripcion, setDescripcion] = useState('');
  
  // Estado para almacenar los menús
  const [menus, setMenus] = useState([]);
  
  // Estado para las acciones (mostrar, ver, editar, eliminar) para cada menú
  const [permissions, setPermissions] = useState({});

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    } else {
      // Cargar los datos del rol si estamos editando
      if (id) {
        fetchRoleData(id);
      }
      fetchMenus();
    }
  }, [id, navigate]);

  // Función para cargar los datos del rol
  const fetchRoleData = async (roleId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };

      const response = await axios.get(`http://localhost:5000/api/roles/roledit/${roleId}`, config);
      const roleData = response.data;

      setRolName(roleData.NOMBRE);
      setDescripcion(roleData.DESCRIPCION);
    } catch (error) {
      console.error('Error al cargar el rol:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los datos del rol.',
      });
    }
  };

  // Función para cargar los menús
  const fetchMenus = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };

      const response = await axios.get('http://localhost:5000/api/navigation/menu', config);
      setMenus(response.data);
    } catch (error) {
      console.error('Error al cargar los menús:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los menús.',
      });
    }
  };

  // Función para manejar el cambio en las casillas de verificación
  const handlePermissionChange = (menuId, action) => {
    setPermissions(prevState => {
      const updatedPermissions = { ...prevState };
      if (!updatedPermissions[menuId]) {
        updatedPermissions[menuId] = { view: false, edit: false, delete: false }; // Inicializamos con todas las acciones
      }
      updatedPermissions[menuId][action] = !updatedPermissions[menuId][action];
      return updatedPermissions;
    });
  };

  // Clasificar menús: Menús principales, submenús y acciones
  const mainMenus = menus.filter(menu => menu.ParentMenuID === 0 && menu.MenuID !== menu.ParentMenuID);
  const subMenus = menus.filter(menu => menu.ParentMenuID !== 0 && menu.MenuID !== menu.ParentMenuID);
  const actions = menus.filter(menu => menu.MenuID === menu.ParentMenuID); // Menús con acciones

  return (
    <Box sx={{ padding: 5 }}>
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" textAlign={'center'} gutterBottom>
        Asignación de permisos
      </Typography>

      {/* Mostrar los datos del rol en un estilo de solo lectura */}
      <div style={{ marginBottom: '20px', marginTop: '20px' }}>
        <Typography variant="h6" gutterBottom>
          Datos del Rol:
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre del Rol"
              value={rolName}
              InputProps={{
                readOnly: true,
                style: { backgroundColor: '#f5f5f5' }, // Fondo gris claro para hacerlo más visible como solo lectura
              }}
              variant="outlined"
              size="small"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              InputProps={{
                readOnly: true,
                style: { backgroundColor: '#f5f5f5' }, // Fondo gris claro
              }}
              variant="outlined"
              size="small"
            />
          </Grid>
        </Grid>
      </div>

      {/* Mostrar los menús principales */}
      <Typography variant="h5" gutterBottom>
        Menús Principales
      </Typography>
      {mainMenus.length === 0 ? (
        <Typography variant="body1">No hay menús principales disponibles.</Typography>
      ) : (
        <Grid container spacing={2}>
          {mainMenus.map((menu) => (
            <Grid item xs={12} sm={6} key={menu._id}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', border: '1px solid #ccc', padding: 2, borderRadius: 1 }}>
                <Typography variant="h6">{menu.DisplayName}</Typography>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={permissions[menu._id]?.view || false}
                      onChange={() => handlePermissionChange(menu._id, 'view')}
                      color="primary"
                    />
                  }
                  label="Mostrar"
                />
              </Box>
            </Grid>
          ))}
        </Grid>
      )}

      {/* Mostrar los submenús */}
      <Typography variant="h5" gutterBottom>
        Submenús
      </Typography>
      {subMenus.length === 0 ? (
        <Typography variant="body1">No hay submenús disponibles.</Typography>
      ) : (
        <Grid container spacing={2}>
          {subMenus.map((menu) => (
            <Grid item xs={12} sm={6} key={menu._id}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', border: '1px solid #ccc', padding: 2, borderRadius: 1 }}>
                <Typography variant="h6">{menu.DisplayName}</Typography>
                <FormControlLabel
                  control={
                    <Checkbox
                      checked={permissions[menu._id]?.view || false}
                      onChange={() => handlePermissionChange(menu._id, 'view')}
                      color="primary"
                    />
                  }
                  label="Mostrar"
                />
              </Box>
            </Grid>
          ))}
        </Grid>
      )}

      {/* Mostrar las acciones (si el MenuID es igual al ParentMenuID) */}
      <Typography variant="h5" gutterBottom>
        Acciones
      </Typography>
      {actions.length === 0 ? (
        <Typography variant="body1">No hay acciones disponibles.</Typography>
      ) : (
        <Grid container spacing={2}>
          {actions.map((menu) => (
            <Grid item xs={12} sm={6} key={menu._id}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', border: '1px solid #ccc', padding: 2, borderRadius: 1 }}>
                <Typography variant="h6">{menu.DisplayName}</Typography>
                <div>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={permissions[menu._id]?.view || false}
                        onChange={() => handlePermissionChange(menu._id, 'view')}
                        color="primary"
                      />
                    }
                    label="Mostrar"
                  />
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={permissions[menu._id]?.edit || false}
                        onChange={() => handlePermissionChange(menu._id, 'edit')}
                        color="primary"
                      />
                    }
                    label="Editar"
                  />
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={permissions[menu._id]?.delete || false}
                        onChange={() => handlePermissionChange(menu._id, 'delete')}
                        color="primary"
                      />
                    }
                    label="Eliminar"
                  />
                </div>
              </Box>
            </Grid>
          ))}
        </Grid>
      )}
    </Box>
  );
