import React, { useState } from 'react';
import { Box, Button, TextField, Typography } from '@mui/material';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate } from 'react-router-dom';

const CrearUsuario = ({ navigation }) => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSubmit = (event) => {
        event.preventDefault();
        // Aquí puedes hacer la llamada a la API para crear el usuario
        console.log("Usuario creado:", { username, email, password });
        navigate('/dashboard/usuarios'); // Redirigir después de crear usuario
    };

    return (
        <AppProvider navigation={navigation}>
            <DashboardLayout>
                <Box
                    component="form"
                    onSubmit={handleSubmit}
                    sx={{ display: 'flex', flexDirection: 'column', maxWidth: 400, margin: '0 auto', mt: 5 }}
                >
                    <Typography variant="h5" align="center" gutterBottom>
                        Crear Usuario
                    </Typography>
                    <TextField
                        label="Nombre de usuario"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        margin="normal"
                        fullWidth
                    />
                    <TextField
                        label="Correo electrónico"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        margin="normal"
                        fullWidth
                    />
                    <TextField
                        label="Contraseña"
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        margin="normal"
                        fullWidth
                    />
                    <Button type="submit" variant="contained" color="primary" sx={{ mt: 2 }}>
                        Crear Usuario
                    </Button>
                </Box>
            </DashboardLayout>
        </AppProvider>
    );
};

export default CrearUsuario;
