import React, { useState, useEffect, useCallback } from 'react';
import { Box, TextField, Button, Grid, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, MenuItem, Select, InputLabel, FormControl } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';
import { CircularProgress } from '@mui/material';

const CrearHito = () => {
    const { proyectoId } = useParams();
    const [showAlert, setShowAlert] = useState(null);
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFin, setFechaFin] = useState('');
    const [estado, setEstado] = useState('Pendiente'); // Estado del hito
    const [proyecto, setProyecto] = useState(null);
    const [hitos, setHitos] = useState([]);
    const navigate = useNavigate();

    const fetchProyecto = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setShowAlert({
                    severity: 'error',
                    message: 'No se encontró el token de autenticación.',
                });
                return;
            }

            const config = { headers: { 'x-auth-token': token } };
            const response = await axios.get(`http://localhost:5000/api/hitos/proyecto/${proyectoId}`, config);

            if (response.data) {
                setProyecto(response.data);
            } else {
                setShowAlert({
                    severity: 'error',
                    message: 'No se encontró el proyecto.',
                });
            }
        } catch (error) {
            console.error('Error al cargar el proyecto:', error.response || error);
            setShowAlert({
                severity: 'error',
                message: 'Hubo un error al cargar el proyecto.',
            });
        }
    }, [proyectoId]);

    const fetchHitos = useCallback(async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setShowAlert({
                    severity: 'error',
                    message: 'No se encontró el token de autenticación.',
                });
                return;
            }

            const config = { headers: { 'x-auth-token': token } };
            const response = await axios.get(`http://localhost:5000/api/hitos/proyecto/${proyectoId}`, config);

            if (response.data) {
                setHitos(response.data);
            } else {
                setHitos([]);
            }
        } catch (error) {
            console.error('Error al cargar los hitos:', error.response || error);
            setShowAlert({
                severity: 'error',
                message: 'Hubo un error al cargar los hitos del proyecto.',
            });
        }
    }, [proyectoId]);

    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login');
        } else {
            fetchProyecto();
            fetchHitos();
        }
    }, [navigate, fetchProyecto, fetchHitos]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!titulo || !descripcion || !fechaInicio || !fechaFin || !proyectoId) {
            setShowAlert({
                severity: 'error',
                message: 'Todos los campos son obligatorios.',
            });
            return;
        }

        const newHito = { titulo, descripcion, fechaInicio, fechaFin, estado, proyectoId };

        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setShowAlert({
                    severity: 'error',
                    message: 'No se encontró el token de autenticación.',
                });
                return;
            }

            const config = { headers: { 'x-auth-token': token } };
            const response = await axios.post('http://localhost:5000/api/hitos', newHito, config);

            if (response.data.msg === 'Hito creado correctamente') {
                setShowAlert({
                    severity: 'success',
                    message: response.data.msg,
                });

                setTitulo('');
                setDescripcion('');
                setFechaInicio('');
                setFechaFin('');
                setEstado('Pendiente'); // Resetear estado

                fetchHitos();

                setTimeout(() => {
                    navigate(`/proyectos/${proyectoId}/hitos`);
                }, 2000);
            } else {
                setShowAlert({
                    severity: 'error',
                    message: response.data.msg || 'Error al crear el hito.',
                });
            }
        } catch (error) {
            console.error('Error al crear el hito:', error.response || error);
            setShowAlert({
                severity: 'error',
                message: 'Hubo un error al crear el hito. Inténtalo de nuevo.',
            });
        }
    };

    return (
        <Box sx={{ padding: 5 }}>
            {showAlert && (
                <AlertMessage
                    severity={showAlert.severity}
                    message={showAlert.message}
                    autoHideDuration={2000}
                    sx={{ mb: 4 }}
                />
            )}

            <Typography variant="h5" gutterBottom>
                {proyecto ? `Proyecto: ${proyecto.nombre}` : <CircularProgress size={24} sx={{ mr: 2 }} />}
            </Typography>

            {/* Formulario de creación de hito */}
            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Título del Hito"
                            value={titulo}
                            onChange={(e) => setTitulo(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Descripción"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de inicio"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaInicio}
                            onChange={(e) => setFechaInicio(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de finalización"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaFin}
                            onChange={(e) => setFechaFin(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel>Estado</InputLabel>
                            <Select
                                value={estado}
                                onChange={(e) => setEstado(e.target.value)}
                                label="Estado"
                            >
                                <MenuItem value="Pendiente">Pendiente</MenuItem>
                                {/* <MenuItem value="En progreso">En progreso</MenuItem>
                                <MenuItem value="Completado">Completado</MenuItem> */}
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            Crear Hito
                        </Button>
                    </Grid>
                </Grid>
            </form>

            {/* Mostrar la tabla de hitos */}
            <Paper sx={{ mt: 4, p: 3 }}>
                <Typography variant="h6" gutterBottom>Hitos del Proyecto</Typography>
                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell><strong>Título</strong></TableCell>
                                <TableCell><strong>Descripción</strong></TableCell>
                                <TableCell><strong>Fecha Inicio</strong></TableCell>
                                <TableCell><strong>Fecha Fin</strong></TableCell>
                                <TableCell><strong>Estado</strong></TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {hitos.length > 0 ? (
                                hitos.map((hito) => (
                                    <TableRow key={hito._id}>
                                        <TableCell>{hito.titulo}</TableCell>
                                        <TableCell>{hito.descripcion}</TableCell>
                                        <TableCell>{new Date(hito.fechaInicio).toLocaleDateString()}</TableCell>
                                        <TableCell>{new Date(hito.fechaFin).toLocaleDateString()}</TableCell>
                                        <TableCell>{hito.estado}</TableCell>
                                    </TableRow>
                                ))
                            ) : (
                                <TableRow>
                                    <TableCell colSpan={5} align="center">
                                        No hay hitos para este proyecto.
                                    </TableCell>
                                </TableRow>
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </Box>
    );
};

export default CrearHito;
