import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';
import { jwtDecode } from 'jwt-decode';

// Registrar las partes necesarias de Chart.js
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const DashboardCharts = () => {
    const [proyectos, setProyectos] = useState([]);
    const [loading, setLoading] = useState(true);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        const decoded = jwtDecode(token);
        const id = decoded.user.id;

        try {
            // Hacer una solicitud GET a la API para obtener los proyectos
            const response = await axios.get(`http://localhost:5000/api/grafica/VencimientoProyecto/${id}`);
            const { labels, datasets } = response.data;

            // Convertir las fechas en días restantes
            const currentDate = new Date();
            const daysRemaining = datasets.map((proyecto, index) => {
                const endDate = new Date(labels[index]);
                const diffTime = endDate - currentDate;
                return Math.ceil(diffTime / (1000 * 60 * 60 * 24)); // Días restantes
            });

            // Guardar los datos para las gráficas
            setProyectos({
                labels: labels, // Fechas de vencimiento
                datasets: daysRemaining, // Días restantes hasta la fecha de vencimiento
                projectNames: datasets, // Nombres de los proyectos
            });
            setLoading(false);
        } catch (error) {
            console.error('Error al obtener los proyectos:', error);
            setLoading(false);
        }
    }, [isAuthenticated, token]);

    useEffect(() => {
        if (isAuthenticated) {
            fetchProyectos();
        }
    }, [isAuthenticated, fetchProyectos]);

    if (loading) return <div>Loading...</div>;

    // Preparar los datos para el gráfico de barras horizontales
    const chartData = {
        labels: proyectos.labels, // Fechas de vencimiento
        datasets: [
            {
                label: 'Días restantes',
                data: proyectos.datasets, // Días restantes
                backgroundColor: proyectos.datasets.map(days => {
                    // Si el proyecto ya venció (días restantes < 0), poner el color en rojo
                    return days < 0 ? 'rgba(255, 99, 132, 0.6)' : 'rgba(75, 192, 192, 0.2)';
                }),
                borderColor: proyectos.datasets.map(days => {
                    return days < 0 ? 'rgba(255, 99, 132, 1)' : 'rgba(75, 192, 192, 1)';
                }),
                borderWidth: 1,
            },
        ],
    };

    return (
        <div>
            <h2>Proyectos Próximos a Vencer</h2>
            <Bar data={chartData} options={{
                indexAxis: 'y', // Esto cambia el gráfico de barras a horizontal
                responsive: true,
                plugins: {
                    legend: {
                        position: 'top',
                    },
                    tooltip: {
                        callbacks: {
                            title: (tooltipItems) => `Proyecto: ${proyectos.projectNames[tooltipItems[0].dataIndex]}`,
                            label: (tooltipItem) => {
                                const days = tooltipItem.raw;
                                return days < 0
                                    ? 'Vencido'
                                    : `Días restantes: ${days}`;
                            },
                        },
                    },
                },
            }} />
        </div>
    );
};

export default DashboardCharts;
