import React, { useState, useEffect, useCallback } from 'react';
import { Box, TextField, Button, Grid, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, MenuItem, Select, InputLabel, FormControl, CircularProgress } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const CrearHito = () => {
    const { proyectoId } = useParams(); // Extraer proyectoId de la URL
    const [showAlert, setShowAlert] = useState(null);
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFin, setFechaFin] = useState('');
    const [estado, setEstado] = useState('Pendiente');
    const [proyecto, setProyecto] = useState(null);
    const [hitos, setHitos] = useState([]);
    const navigate = useNavigate();

    // Función general para realizar peticiones
    const makeRequest = async (method, url, data = {}) => {
        const token = localStorage.getItem('token');
        if (!token) {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el token de autenticación.',
            });
            return;
        }

        const config = { headers: { 'x-auth-token': token } };
        try {
            const response = await axios[method](url, data, config);
            return response.data;
        } catch (error) {
            console.error('Error:', error.response || error);
            setShowAlert({
                severity: 'error',
                message: error.response?.data?.msg || 'Hubo un error al realizar la solicitud.',
            });
            return null;
        }
    };

    // Obtener proyecto y hitos
    const fetchProyecto = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        if (data) {
            setProyecto(data);
        } else {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el proyecto.',
            });
        }
    }, [proyectoId]);

    const fetchHitos = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        if (data) {
            setHitos(data);
        } else {
            setHitos([]);
        }
    }, [proyectoId]);

    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login');
        } else {
            fetchProyecto();
            fetchHitos();
        }
    }, [navigate, fetchProyecto, fetchHitos]);

    // Manejo del formulario
    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!titulo || !descripcion || !fechaInicio || !fechaFin) {
            setShowAlert({
                severity: 'error',
                message: 'Todos los campos son obligatorios.',
            });
            return;
        }

        // Verifica que proyectoId tenga un valor válido
        if (!proyectoId) {
            setShowAlert({
                severity: 'error',
                message: 'ID del proyecto no encontrado.',
            });
            return;
        }

        // Crear el nuevo hito con el proyectoId incluido
        const newHito = { 
            titulo, 
            descripcion, 
            fechaInicio, 
            fechaFin, 
            estado,
            proyectoId // Agregar el proyectoId al cuerpo de la solicitud
        };

        // Realizar la solicitud POST
        const response = await makeRequest('post', 'http://localhost:5000/api/hitos/register', newHito);

        if (response && response.msg === 'Hito creado correctamente') {
            setShowAlert({
                severity: 'success',
                message: response.msg,
            });

            // Limpiar el formulario
            setTitulo('');
            setDescripcion('');
            setFechaInicio('');
            setFechaFin('');
            setEstado('Pendiente');

            // Obtener la lista de hitos actualizada
            fetchHitos();
        } else {
            setShowAlert({
                severity: 'error',
                message: response?.msg || 'Error al crear el hito.',
            });
        }
    };

    return (
        <Box sx={{ padding: 5 }}>
            {showAlert && (
                <AlertMessage
                    severity={showAlert.severity}
                    message={showAlert.message}
                    autoHideDuration={2000}
                    sx={{ mb: 4 }}
                />
            )}

            <Typography variant="h5" gutterBottom>
                {proyecto ? `Proyecto: ${proyecto.nombre}` : <CircularProgress size={24} sx={{ mr: 2 }} />}
            </Typography>

            {/* Formulario de creación de hito */}
            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Título del Hito"
                            value={titulo}
                            onChange={(e) => setTitulo(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Descripción"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de inicio"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaInicio}
                            onChange={(e) => setFechaInicio(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de finalización"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaFin}
                            onChange={(e) => setFechaFin(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel>Estado</InputLabel>
                            <Select
                                value={estado}
                                onChange={(e) => setEstado(e.target.value)}
                                label="Estado"
                            >
                                <MenuItem value="Pendiente">Pendiente</MenuItem>
                                <MenuItem value="En Proceso">En Proceso</MenuItem>
                                <MenuItem value="Finalizado">Finalizado</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            Crear Hito
                        </Button>
                    </Grid>
                </Grid>
            </form>

            {/* Mostrar la tabla de hitos */}
            <Paper sx={{ mt: 4, p: 3 }}>
                <Typography variant="h6" gutterBottom>Hitos del Proyecto</Typography>
                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell><strong>Título</strong></TableCell>
                                <TableCell><strong>Descripción</strong></TableCell>
                                <TableCell><strong>Fecha Inicio</strong></TableCell>
                                <TableCell><strong>Fecha Fin</strong></TableCell>
                                <TableCell><strong>Estado</strong></TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {hitos.length > 0 ? (
                                hitos.map((hito) => (
                                    <TableRow key={hito._id}>
                                        <TableCell>{hito.titulo}</TableCell>
                                        <TableCell>{hito.descripcion}</TableCell>
                                        <TableCell>{hito.fechaInicio}</TableCell>
                                        <TableCell>{hito.fechaFin}</TableCell>
                                        <TableCell>{hito.estado}</TableCell>
                                    </TableRow>
                                ))
                            ) : (
                                <TableRow>
                                    <TableCell colSpan={5} align="center">No hay hitos registrados.</TableCell>
                                </TableRow>
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </Box>
    );
};

export default CrearHito;
