import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import DashboardLayoutNavigationLinks from './components/DashboardPageLayout';
import Login from './pages/Login'; // Asegúrate de tener esta página
import Usuario from './pages/Seguridad/usuario'; // Asegúrate de tener la página de Usuarios
import { isAuthenticated } from './utils/auth'; // Importar isAuthenticated para verificar el token

function App() {
    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route path="/login" element={<Login />} />

                {/* Rutas del dashboard */}
                <Route path="/dashboard" element={<DashboardLayoutNavigationLinks />}>
                    {/* Rutas internas del Dashboard */}
                    <Route path="usuarios" element={<Usuario />} />
                    {/* Añadir otras rutas según sea necesario */}
                </Route>

                {/* Redirigir al login si la ruta no existe */}
                <Route path="*" element={<Login />} />
            </Routes>
        </Router>
    );
}

export default App;
