import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import { Chart as ChartJS, ArcElement, Tooltip, Legend } from 'chart.js';
import { jwtDecode } from 'jwt-decode';

ChartJS.register(ArcElement, Tooltip, Legend);

const estadosPosibles = ['Aprobado', 'Rechazada', 'Pendiente', 'Bloqueado', 'Desconocido', 'Requiere más pruebas', 'No aplicable'];

const DashboardChartsPrueba = () => {
    const [proyectos, setProyectos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [selectedEstado, setSelectedEstado] = useState(estadosPosibles[0]);
    const [noData, setNoData] = useState(false);

    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        setLoading(true);
        setNoData(false);
        setError(null);

        try {
            const decoded = jwtDecode(token);
            const id = decoded.user?.id;

            const config = {
                headers: {
                    'x-auth-token': token,
                },
            };

            const response = await axios.get(`http://localhost:5000/api/grafica/EstadoPrueba/${id}`, config);
            const { estadoProyectos } = response.data;

            if (!estadoProyectos || Object.values(estadoProyectos).every(count => count === 0)) {
                setNoData(true);
                setProyectos([]);
                return;
            }

            setProyectos(estadoProyectos);
            setNoData(false);
        } catch (err) {
            console.error('Error al obtener los proyectos:', err);
            setError('No se pudieron cargar los proyectos');
            setProyectos([]);
            setNoData(true);
        } finally {
            setLoading(false);
        }
    }, [isAuthenticated, token]);

    useEffect(() => {
        fetchProyectos();
    }, [fetchProyectos]);

    // Filtrar proyectos según el estado seleccionado
    const proyectosPorEstado = proyectos.filter(proyecto => proyecto.estado_prueba === selectedEstado);

    // Preparar datos para el gráfico
    const chartData = {
        labels: estadosPosibles,
        datasets: [{
            data: estadosPosibles.map(estado => proyectos.filter(p => p.estado_prueba === estado).length), // Número de proyectos por estado
            backgroundColor: ['green', 'red', 'yellow', 'orange', 'gray', 'purple', 'blue'], // Colores para cada estado
        }],
    };

    const handleEstadoChange = (e) => {
        setSelectedEstado(e.target.value);
    };

    if (loading) return <div>Cargando datos...</div>;

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Proyectos por Estado</h2>
            <div style={{ marginBottom: '20px', display: 'flex', justifyContent: 'center', alignItems: 'center', gap: '10px' }}>
                <select
                    value={selectedEstado}
                    onChange={handleEstadoChange}
                    style={{ padding: '5px', width: '200px' }}
                >
                    {estadosPosibles.map(estado => (
                        <option key={estado} value={estado}>{estado}</option>
                    ))}
                </select>
            </div>

            {noData && (
                <div style={{ textAlign: 'center', fontSize: '18px', color: '#888' }}>
                    <p>No hay proyectos en el estado seleccionado. Intenta con otro estado.</p>
                </div>
            )}

            {error && (
                <div style={{ textAlign: 'center', fontSize: '18px', color: 'red' }}>
                    <p>{error}</p>
                </div>
            )}

            <Pie
                data={chartData}
                options={{
                    responsive: true,
                    plugins: {
                        legend: {
                            position: 'top',
                        },
                        tooltip: {
                            callbacks: {
                                label: (tooltipItem) => {
                                    const index = tooltipItem.dataIndex;
                                    const label = chartData.labels[index] || 'Desconocido';
                                    const value = tooltipItem.raw;
                                    return `${label}: ${value} Proyectos`;
                                },
                            },
                        },
                    },
                }}
            />

            {proyectosPorEstado.length > 0 && (
                <div style={{ marginTop: '20px' }}>
                    <h3 style={{ textAlign: 'center' }}>Proyectos en estado: {selectedEstado}</h3>
                    <ul style={{ listStyleType: 'none', padding: 0 }}>
                        {proyectosPorEstado.map((proyecto, index) => (
                            <li key={index} style={{ padding: '5px', border: '1px solid #ccc', margin: '5px 0' }}>
                                {proyecto.nombre}
                            </li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
};

export default DashboardChartsPrueba;
