import React, { useState, useEffect, useCallback } from 'react';
import {
    Box,
    TextField,
    Button,
    Grid,
    Typography,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    MenuItem,
    Select,
    InputLabel,
    FormControl,
    CircularProgress,
    IconButton,
    Alert,
} from '@mui/material';
import { Edit, Delete } from '@mui/icons-material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { isAuthenticated } from '../../utils/auth';

const CrearHito = () => {
    const { proyectoId } = useParams();
    const [showAlert, setShowAlert] = useState(null);
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFin, setFechaFin] = useState('');
    const [estado, setEstado] = useState('Pendiente');
    const [proyecto, setProyecto] = useState(null);
    const [hitos, setHitos] = useState([]);
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    const makeRequest = async (method, url, data = {}) => {
        const token = localStorage.getItem('token');
        if (!token) {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el token de autenticación.',
            });
            return null;
        }

        const config = { headers: { 'x-auth-token': token } };
        try {
            const response = await axios[method](url, data, config);
            return response.data;
        } catch (error) {
            setShowAlert({
                severity: 'error',
                message: error.response?.data?.msg || 'Error inesperado en la solicitud.',
            });
            return null;
        }
    };

    const fetchProyecto = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        if (data) {
            setProyecto(data);
        } else {
            setShowAlert({
                severity: 'warning',
                message: 'No se encontró el proyecto solicitado.',
            });
        }
    }, [proyectoId]);

    const fetchHitos = useCallback(async () => {
        setLoading(true);
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/hito/${proyectoId}`);
        setLoading(false);
        if (data) {
            setHitos(data);
        } else {
            setHitos([]);
        }
    }, [proyectoId]);

    const handleDelete = async (id_hito) => {
        const confirmed = window.confirm('¿Estás seguro de que deseas eliminar este hito?');
        if (!confirmed) return;

        const response = await makeRequest('delete', `http://localhost:5000/api/hitos/${id_hito}`);
        if (response) {
            setShowAlert({
                severity: 'success',
                message: 'Hito eliminado correctamente.',
            });
            fetchHitos();
        }
    };

    const handleEdit = (hito) => {
        setTitulo(hito.nombre_hito);
        setDescripcion(hito.descripcion);
        setFechaInicio(hito.fecha_inicio.split('T')[0]);
        setFechaFin(hito.fecha_fin.split('T')[0]);
        setEstado(hito.estado);
    };

    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login');
        } else {
            fetchProyecto();
            fetchHitos();
        }
    }, [navigate, fetchProyecto, fetchHitos]);

    useEffect(() => {
        if (showAlert) {
            const timer = setTimeout(() => setShowAlert(null), 3000);
            return () => clearTimeout(timer);
        }
    }, [showAlert]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!titulo || !descripcion || !fechaInicio || !fechaFin) {
            setShowAlert({
                severity: 'error',
                message: 'Todos los campos son obligatorios.',
            });
            return;
        }

        if (new Date(fechaInicio) > new Date(fechaFin)) {
            setShowAlert({
                severity: 'error',
                message: 'La fecha de inicio no puede ser posterior a la fecha de finalización.',
            });
            return;
        }

        const newHito = { titulo, descripcion, fechaInicio, fechaFin, estado, proyectoId };
        const response = await makeRequest('post', 'http://localhost:5000/api/hitos/register', newHito);
        if (response) {
            setShowAlert({
                severity: 'success',
                message: 'Hito creado exitosamente.',
            });
            setTitulo('');
            setDescripcion('');
            setFechaInicio('');
            setFechaFin('');
            setEstado('Pendiente');
            fetchHitos();
        }
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()}`;
    };

    return (
        <Box sx={{ padding: 5 }}>
            {showAlert && (
                <Alert severity={showAlert.severity} sx={{ mb: 4 }}>
                    {showAlert.message}
                </Alert>
            )}

            <Typography variant="h5" gutterBottom>
                {proyecto ? `Proyecto: ${proyecto.nombre}` : <CircularProgress size={24} />}
            </Typography>

            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    {/* Campos del formulario */}
                </Grid>
            </form>

            <Paper sx={{ mt: 4, p: 3 }}>
                <Typography variant="h6" gutterBottom>Hitos del Proyecto</Typography>
                <TableContainer>
                    <Table>
                        {/* Estructura de tabla */}
                    </Table>
                </TableContainer>
            </Paper>
        </Box>
    );
};

export default CrearHito;
