import React, { useState, useEffect } from 'react';
import {
  Box,
  TextField,
  Button,
  Grid,
  Typography,
  MenuItem,
  Select,
  InputLabel,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableFooter,
  TablePagination,
  TableRow,
  TableHead,
  Paper,
  IconButton,
  Dialog,
  DialogContent,
} from '@mui/material';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import VisibilityIcon from '@mui/icons-material/Visibility';
import ConfirmFinDialog from '../../components/ConfirmFinalizar'

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null);
  const [hitos, setHitos] = useState([]);
  const [hitoSeleccionado, setHitoSeleccionado] = useState('');
  const [descripcionHito, setDescripcionHito] = useState('');
  const [casos, setCasos] = useState([]);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(0);
  const [rowsPerPage, setRowsPerPage] = useState(5);
  const [openImageDialog, setOpenImageDialog] = useState(false);
  const [selectedImage, setSelectedImage] = useState('');
  const { id } = useParams();
  const [isLoading, setIsLoading] = useState(true);
  const [showCases, setShowCases] = useState(false);
  const [openConfirmDialog, setOpenConfirmDialog] = useState(false); // Estado para controlar el diálogo de confirmación
  const [hitoFinalizar, setHitoFinalizar] = useState(null); // Estado para almacenar el hito que se va a finalizar

  useEffect(() => {
    const fetchHitos = async () => {
      setIsLoading(true);
      try {
        const response = await axios.get(`http://localhost:5000/api/pruebas/hitosporPruebas/${id}`);
        setHitos(response.data || []);
      } catch (error) {
        console.error('Error al obtener los hitos:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los hitos del proyecto.',
        });
      } finally {
        setIsLoading(false);
      }
    };

    if (id) {
      fetchHitos();
    }
  }, [id]);

  const handleHitoSeleccionado = (e) => {
    const hitoId = e.target.value;
    setHitoSeleccionado(hitoId);

    const hito = hitos.find((hito) => hito.id_hito === hitoId);
    setDescripcionHito(hito ? hito.descripcion : '');
  };

  const handleVerCasos = async () => {
    if (!hitoSeleccionado) {
      setShowAlert({
        severity: 'warning',
        message: 'Por favor, selecciona un hito.',
      });
      return;
    }

    setIsLoading(true);
    try {
      const response = await axios.get(`http://localhost:5000/api/pruebas/casos/${id}/${hitoSeleccionado}`);
      setCasos(response.data || []);
      setShowAlert({
        severity: 'success',
        message: 'Casos cargados exitosamente.',
      });
      setShowCases(true);
    } catch (error) {
      console.error('Error al obtener los casos:', error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los casos del hito.',
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handlePageChange = (event, newPage) => {
    setPage(newPage);
  };

  const handleRowsPerPageChange = (event) => {
    setRowsPerPage(parseInt(event.target.value, 10));
    setPage(0);
  };

  const filteredCasos = casos.filter((caso) => {
    return (
      caso.resultado_esperado?.toLowerCase().includes(search.toLowerCase()) ||
      caso.prueba_realizada?.toLowerCase().includes(search.toLowerCase())
    );
  });

  const emptyRows = page > 0 ? Math.max(0, (1 + page) * rowsPerPage - filteredCasos.length) : 0;

  const handleOpenImageDialog = (image) => {
    setSelectedImage(image);
    setOpenImageDialog(true);
  };

  const handleCloseImageDialog = () => {
    setOpenImageDialog(false);
    setSelectedImage('');
  };

  // Función para abrir el diálogo de confirmación
  const handleOpenConfirmDialog = () => {
    setHitoFinalizar(hitoSeleccionado); // Guarda el hito que se quiere finalizar
    setOpenConfirmDialog(true); // Abre el cuadro de confirmación
  };


   // Función para cerrar el cuadro de confirmación y manejar la acción
   const handleCloseConfirmDialog = async (confirmed) => {
    setOpenConfirmDialog(false);
    if (confirmed && hitoFinalizar) {
      try {
        // Enviar el ID del hito para finalizarlo
        await axios.post('http://localhost:5000/api/pruebas/finalizarHito', { id_hito: hitoFinalizar });

        setShowAlert({
          severity: 'success',
          message: 'Hito finalizado exitosamente.',
        });
        setHitoSeleccionado(''); // Limpiar selección
        setDescripcionHito(''); // Limpiar descripción del hito
      } catch (error) {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al finalizar el hito.',
        });
      }
    } else {
      // Si se canceló, cerrar la alerta
      setShowAlert(null);
    }
  };

  // Close alert after 3 seconds if it is shown
  useEffect(() => {
    if (showAlert) {
      const timer = setTimeout(() => {
        setShowAlert(null);
      }, 2000);
      return () => clearTimeout(timer);
    }
  }, [showAlert]);

  return (
    <Box sx={{ padding: 5 }}>
      <Typography variant="h4" gutterBottom align="center">
        Plan de Pruebas
      </Typography>

      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          onClose={() => setShowAlert(null)}
        />
      )}

      <form>
        <Grid container spacing={2} mt={3}>
          <Grid item xs={12}>
            <InputLabel>Selecciona un Hito</InputLabel>
            <Select
              fullWidth
              value={hitoSeleccionado}
              onChange={handleHitoSeleccionado}
              displayEmpty
              required
              disabled={isLoading}
            >
              <MenuItem value="" disabled>
                Selecciona un hito...
              </MenuItem>
              {hitos.map((hito) => (
                <MenuItem key={hito.id_hito} value={hito.id_hito}>
                  {hito.nombre_hito}
                </MenuItem>
              ))}
            </Select>
          </Grid>

          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Descripción del Hito"
              value={descripcionHito}
              disabled
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12}>
            <Button
              variant="contained"
              fullWidth
              onClick={handleVerCasos}
              disabled={isLoading}
            >
              Ver Casos
            </Button>
          </Grid>

          {showCases && (
            <>
              <Grid item xs={10}>
                <TextField
                  label="Buscar Caso"
                  variant="outlined"
                  value={search}
                  onChange={(e) => setSearch(e.target.value)}
                  fullWidth
                  disabled={isLoading}
                />
              </Grid>

              <Grid item xs={2}>
                <Button
                  variant="contained"
                  color="error"
                  fullWidth
                  sx={{ height: '52px' }}
                  onClick={handleOpenConfirmDialog} // Abre el cuadro de confirmación
                  disabled={isLoading}
                >
                  Finalizar Hito
                </Button>
              </Grid>
            </>
          )}
        </Grid>
      </form>

      {showCases && (
        <TableContainer component={Paper} sx={{ marginTop: 3 }}>
          <Table sx={{ minWidth: 500 }}>
            <TableHead>
              <TableRow>
                <TableCell>Caso</TableCell>
                <TableCell align="left">Hito</TableCell>
                <TableCell align="left">Resultado Esperado</TableCell>
                <TableCell align="left">Prueba Realizada</TableCell>
                <TableCell align="left">Estado de la Prueba</TableCell>
                <TableCell align="left">Criterio de Aceptación</TableCell>
                <TableCell align="left">Evidencia</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {(rowsPerPage > 0
                ? filteredCasos.slice(page * rowsPerPage, page * rowsPerPage + rowsPerPage)
                : filteredCasos
              ).map((caso) => (
                <TableRow key={caso.id_plan}>
                  <TableCell>{caso.id_plan}</TableCell>
                  <TableCell>{caso.nombre_hito}</TableCell>
                  <TableCell>{caso.resultado_esperado}</TableCell>
                  <TableCell>{caso.prueba_realizada}</TableCell>
                  <TableCell>{caso.estado_prueba}</TableCell>
                  <TableCell>{caso.criterio_aceptacion}</TableCell>
                  <TableCell>
                  <IconButton onClick={() => handleOpenImageDialog(caso.imagen_prueba)} disabled={isLoading}> 
                    <VisibilityIcon />
                  </IconButton>
                </TableCell>
                </TableRow>
              ))}
              {emptyRows > 0 && (
                <TableRow style={{ height: 53 * emptyRows }}>
                  <TableCell colSpan={6} />
                </TableRow>
              )}
            </TableBody>
            <TableFooter>
              <TableRow>
                <TablePagination
                  rowsPerPageOptions={[5, 10, 25]}
                  colSpan={7}
                  count={filteredCasos.length}
                  rowsPerPage={rowsPerPage}
                  page={page}
                  onPageChange={handlePageChange}
                  onRowsPerPageChange={handleRowsPerPageChange}
                />
              </TableRow>
            </TableFooter>
          </Table>
        </TableContainer>
      )}

      <Dialog
        open={openImageDialog}
        onClose={handleCloseImageDialog}
        fullWidth
        maxWidth="md"
      >
        <DialogContent>
        {selectedImage ? (
            <img
              src={selectedImage}
              alt="Evidencia"
              style={{ width: '100%', height: 'auto' }}
            />
          ) : (
            <Typography>No hay evidencia disponible</Typography>
          )}        </DialogContent>
      </Dialog>

      <ConfirmFinDialog
        open={openConfirmDialog}
        onClose={handleCloseConfirmDialog} // Cierra el diálogo y maneja la acción
      />
    </Box>
  );
};

export default CrearPlanDePrueba;
