import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import DashboardLayoutNavigationLinks from './components/DashboardPageLayout';
import Login from './pages/Login'; 
import Usuario from './pages/Seguridad/usuario'; 
import { isAuthenticated } from './utils/auth'; 

function App() {
    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route path="/login" element={<Login />} />

                {/* Redirigir al login si no está autenticado */}
                {/* Ruta para el Dashboard (sin el prefijo "/dashboard") */}
                <Route path="/" element={isAuthenticated() ? <DashboardLayoutNavigationLinks /> : <Navigate to="/login" />} >
                    {/* Aquí agregamos las rutas internas, como /seguridad/usuarios */}
                    <Route path="seguridad/usuarios" element={<Usuario />} />
                    {/* Puedes agregar otras rutas como /seguridad/roles, etc. */}
                </Route>

                {/* Redirigir cualquier ruta no encontrada al login */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
