// src/theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',  // Color primario personalizado
    },
    secondary: {
      main: '#dc004e',  // Color secundario personalizado
    },
  },
});

export default theme;
