import React, { useState, useEffect, useCallback } from 'react';
import {
    Box, TextField, Button, Grid, Typography, Paper,
    Table, TableBody, TableCell, TableContainer, TableHead, TableRow,
    MenuItem, Select, InputLabel, FormControl, CircularProgress,
} from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const CrearHito = () => {
    const { proyectoId } = useParams();
    const navigate = useNavigate();
    const [showAlert, setShowAlert] = useState(null);
    const [alertTimeout, setAlertTimeout] = useState(null);
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFin, setFechaFin] = useState('');
    const [estado, setEstado] = useState('Pendiente');
    const [proyecto, setProyecto] = useState(null);
    const [hitos, setHitos] = useState([]);

    // Función para manejar alertas
    const handleAlert = (severity, message, duration = 2000) => {
        if (alertTimeout) clearTimeout(alertTimeout); // Limpiar alertas previas
        setShowAlert({ severity, message });
        const timeout = setTimeout(() => setShowAlert(null), duration);
        setAlertTimeout(timeout);
    };

    // Petición general
    const makeRequest = async (method, url, data = {}) => {
        const token = localStorage.getItem('token');
        if (!token) {
            handleAlert('error', 'No se encontró el token de autenticación.');
            return null;
        }

        try {
            const response = await axios[method](url, data, {
                headers: { 'x-auth-token': token },
            });
            return response.data;
        } catch (error) {
            handleAlert('error', error.response?.data?.msg || 'Error en la solicitud.');
            return null;
        }
    };

    // Fetch Proyecto
    const fetchProyecto = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        if (data) {
            setProyecto(data);
        } else {
            handleAlert('error', 'No se encontró el proyecto.');
        }
    }, [proyectoId]);

    // Fetch Hitos
    const fetchHitos = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        setHitos(data || []);
    }, [proyectoId]);

    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login');
        } else {
            fetchProyecto();
            fetchHitos();
        }
    }, [navigate, fetchProyecto, fetchHitos]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!titulo || !descripcion || !fechaInicio || !fechaFin) {
            handleAlert('error', 'Todos los campos son obligatorios.');
            return;
        }

        const newHito = { titulo, descripcion, fechaInicio, fechaFin, estado, proyectoId };
        const response = await makeRequest('post', 'http://localhost:5000/api/hitos/register', newHito);

        if (response?.success) {
            handleAlert('success', response.msg || 'Hito creado con éxito.');
            setTitulo('');
            setDescripcion('');
            setFechaInicio('');
            setFechaFin('');
            setEstado('Pendiente');
            fetchHitos();
        }
    };

    return (
        <Box sx={{ padding: 5 }}>
            {showAlert && (
                <AlertMessage
                    severity={showAlert.severity}
                    message={showAlert.message}
                    autoHideDuration={2000}
                />
            )}

            <Typography variant="h5" gutterBottom>
                {proyecto ? `Proyecto: ${proyecto.nombre}` : <CircularProgress size={24} />}
            </Typography>

            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Título del Hito"
                            value={titulo}
                            onChange={(e) => setTitulo(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Descripción"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de inicio"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaInicio}
                            onChange={(e) => setFechaInicio(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de finalización"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaFin}
                            onChange={(e) => setFechaFin(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel>Estado</InputLabel>
                            <Select
                                value={estado}
                                onChange={(e) => setEstado(e.target.value)}
                                label="Estado"
                            >
                                <MenuItem value="Pendiente">Pendiente</MenuItem>
                                <MenuItem value="En Proceso">En Proceso</MenuItem>
                                <MenuItem value="Finalizado">Finalizado</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            Crear Hito
                        </Button>
                    </Grid>
                </Grid>
            </form>

            <Paper sx={{ mt: 4, p: 3 }}>
                <Typography variant="h6" gutterBottom>Hitos del Proyecto</Typography>
                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell><strong>Título</strong></TableCell>
                                <TableCell><strong>Descripción</strong></TableCell>
                                <TableCell><strong>Fecha Inicio</strong></TableCell>
                                <TableCell><strong>Fecha Fin</strong></TableCell>
                                <TableCell><strong>Estado</strong></TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {hitos.length > 0 ? (
                                hitos.map((hito) => (
                                    <TableRow key={hito._id}>
                                        <TableCell>{hito.titulo}</TableCell>
                                        <TableCell>{hito.descripcion}</TableCell>
                                        <TableCell>{hito.fechaInicio}</TableCell>
                                        <TableCell>{hito.fechaFin}</TableCell>
                                        <TableCell>{hito.estado}</TableCell>
                                    </TableRow>
                                ))
                            ) : (
                                <TableRow>
                                    <TableCell colSpan={5}>No hay hitos registrados.</TableCell>
                                </TableRow>
                            )}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>
        </Box>
    );
};

export default CrearHito;
