import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const CrearRoles = () => {
    const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
    const navigate = useNavigate();

    // Estados para los campos del formulario
    const [rolName, setRolName] = useState('');
    const [descripcion, setDescripcion] = useState('');

    // Verificar si el usuario está autenticado
    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login'); // Redirige al login si no está autenticado
        }
    }, [navigate]);

    // Función para manejar el envío del formulario
    const handleSubmit = async (e) => {
        e.preventDefault();

        // Validaciones simples
        if (!rolName || !descripcion) {
            setShowAlert({
                severity: 'error',
                message: 'Todos los campos son obligatorios.'
            });
            return;
        }

        const newRole = {
            rolName,
            descripcion
        };

        try {
            // Enviar la solicitud al backend para crear un nuevo rol
            const response = await axios.post('http://localhost:5000/api/roles/register', newRole); // URL del endpoint para crear roles
            
            // Verificar si la API ha devuelto un mensaje de error
            if (response.data.msg) {
                setShowAlert({ severity: 'error', message: response.data.msg }); // Mostrar el mensaje de error
            } else {
                setShowAlert({ severity: 'success', message: 'Rol creado exitosamente' }); // Mensaje de éxito

                // Resetea los campos del formulario solo si la creación fue exitosa
                setRolName('');
                setDescripcion('');

                // Redirige después de 3 segundos
                setTimeout(() => navigate('/Seguridad/roles'), 3000);
            }
        } catch (error) {
            // Mostrar el error completo
            console.error('Error al crear el rol:', error.response || error); // Mostrar detalles del error

            // Si la respuesta de error tiene un mensaje específico
            if (error.response && error.response.data && error.response.data.msg) {
                setShowAlert({ severity: 'error', message: error.response.data.msg }); // Mostrar el mensaje específico de la API
            } else {
                setShowAlert({ severity: 'error', message: 'Hubo un error al crear el rol. Inténtalo de nuevo.' });
            }
        }

        // Cerrar la alerta después de 3 segundos
        setTimeout(() => {
            setShowAlert(null);
        }, 3000);
    };

    return (
        <Box sx={{ padding: 5 }}>
            {/* Mostrar la alerta si hay alguna */}
            {showAlert && (
                <AlertMessage
                    severity={showAlert.severity}
                    message={showAlert.message}
                    autoHideDuration={3000} // La alerta se cierra después de 3 segundos
                    sx={{ mb: 4 }} // Agregar margen inferior a la alerta
                />
            )}

            <Typography variant="h4" gutterBottom>
                Crear Nuevo Rol
            </Typography>

            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    {/* Campo para Nombre del Rol */}
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Nombre del Rol"
                            value={rolName}
                            onChange={(e) => setRolName(e.target.value)}
                            required
                        />
                    </Grid>

                    {/* Campo para Descripción */}
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Descripción"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            required
                        />
                    </Grid>

                    {/* Botón para enviar el formulario */}
                    <Grid item xs={12}>
                        <Button
                            type="submit"
                            variant="contained"
                            color="primary"
                            fullWidth
                        >
                            Crear Rol
                        </Button>
                    </Grid>
                </Grid>
            </form>
        </Box>
    );
};

export default CrearRoles;
