// src/components/DashboardLayoutNavigationLinks.js
import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import Usuario from '../pages/'; // Verifica la ruta
import Welcome from '../components/Welcome'; // Componente de bienvenida

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchMenus = useCallback(async () => {
        if (!token) return;

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    }, [token]);

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    return (
        <div>
            <Routes>
                {menuItems.map((item) => (
                    <Route
                        key={item.MenuID}
                        path={`/dashboard/${item.MenuURL}`} // Asegúrate de que MenuURL sea correcto
                        element={item.MenuURL === 'seguridad/usuarios' ? <Usuario /> : <Welcome />}
                    />
                ))}
                <Route path="/dashboard" element={<Welcome />} />
                <Route path="*" element={<Welcome />} />
            </Routes>
        </div>
    );
}
