import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import {jwtDecode} from 'jwt-decode';

const DashboardLayoutNavigationLinks = () => {
    const [menuItems, setMenuItems] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            navigate('/login'); // Redirigir al login si no hay token
        } else {
            fetchMenus(); // Cargar menús si hay token
        }
    }, []);

    const fetchMenus = async () => {
        const token = localStorage.getItem('token');
        if (!token) return;

        const decoded = jwtDecode(token);
        const username = decoded.user.username; // Obtén el nombre de usuario del token

        try {
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`, {
                headers: {
                    Authorization: `Bearer ${token}`, // Enviar el token
                },
            });
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
            // Redirigir al login si hay un error de autenticación
            if (error.response && error.response.status === 401) {
                navigate('/login');
            }
        }
    };

    const handleMenuClick = (item) => {
        if (item.DisplayName.toLowerCase() === 'usuarios') {
            // Aquí deberías cargar tu formulario de usuarios
            setCurrentComponent(<UsuarioForm />); // Componente del formulario de usuario
        } else {
            navigate(item.MenuURL); // Navegar a la URL especificada
        }
    };

    return (
        <div>
            {menuItems.map(item => (
                <button key={item.MenuID} onClick={() => handleMenuClick(item)}>
                    {item.DisplayName}
                </button>
            ))}
        </div>
    );
};

export default DashboardLayoutNavigationLinks;
