import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    CategoryScale,
    LinearScale,
    BarElement,
    Title,
    Tooltip,
    Legend,
} from 'chart.js';

// Registrar las partes necesarias de Chart.js
ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

const DashboardCharts = () => {
    const [proyectos, setProyectos] = useState([]);
    const [loading, setLoading] = useState(true);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            // Hacer una solicitud GET a la API para obtener los proyectos
            const response = await axios.get(`http://localhost:5000/api/grafica/VencimientoProyecto/${username}`);
            const { labels, datasets } = response.data;

            // Guardar los datos para las gráficas
            setProyectos({ labels, datasets });
            setLoading(false);
        } catch (error) {
            console.error('Error al obtener los proyectos:', error);
            setLoading(false);
        }
    }, [isAuthenticated, token]);

    useEffect(() => {
        if (isAuthenticated) {
            fetchProyectos();
        }
    }, [isAuthenticated, fetchProyectos]);

    if (loading) return <div>Loading...</div>;

    const chartData = {
        labels: proyectos.labels,
        datasets: [
            {
                label: 'Cantidad de Proyectos',
                data: proyectos.datasets,
                backgroundColor: 'rgba(75, 192, 192, 0.2)',
                borderColor: 'rgba(75, 192, 192, 1)',
                borderWidth: 1,
            },
        ],
    };

    return (
        <div>
            <h2>Proyectos por Semana</h2>
            <Bar data={chartData} />
        </div>
    );
};

export default DashboardCharts;
