import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, Snackbar, Alert } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { isAuthenticated } from '../../utils/auth'; // Función para verificar autenticación

const CrearProyecto = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const navigate = useNavigate();

  // Estados para los campos del formulario
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFinalizacion, setFechaFinalizacion] = useState('');
  const [estado, setEstado] = useState('En progreso');
  const [presupuesto, setPresupuesto] = useState('');
  const [idResponsable, setIdResponsable] = useState('');

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    // Si el usuario no está autenticado, redirige al login
    if (!isAuthenticated()) {
      navigate('/login');
    }
  }, [navigate]);

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples
    if (!nombre || !descripcion || !fechaInicio || !fechaFinalizacion || !presupuesto || !idResponsable) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const newProyecto = {
      nombre,
      descripcion,
      fecha_inicio: fechaInicio,
      fecha_finalizacion: fechaFinalizacion,
      estado,
      presupuesto,
      id_responsable: idResponsable,
    };

    try {
      // Obtener el token JWT desde el almacenamiento local o cookies
      const token = localStorage.getItem('token');

      // Verificar si el token existe
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      // Configuración del encabezado con el token
      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };

      const response = await axios.post('http://localhost:5000/api/proyectos', newProyecto, config);

      // Verificar la respuesta de la API
      if (response.data.msg === 'El proyecto ya existe') {
        setShowAlert({
          severity: 'error',
          message: response.data.msg, // Mostrar el mensaje de error
        });
      } else if (response.data.msg === 'Proyecto registrado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg, // Mostrar el mensaje de éxito
        });

        // Limpiar los campos del formulario si el proyecto se creó con éxito
        setNombre('');
        setDescripcion('');
        setFechaInicio('');
        setFechaFinalizacion('');
        setEstado('En progreso');
        setPresupuesto('');
        setIdResponsable('');

        // Redirigir a la lista de proyectos después de 3 segundos
        setTimeout(() => {
          navigate('/proyectos');
        }, 2000);
      }
    } catch (error) {
      console.error('Error al crear el proyecto:', error.response || error);

      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el proyecto. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <Snackbar
          open={showAlert !== null}
          autoHideDuration={2000}
          onClose={() => setShowAlert(null)}
        >
          <Alert severity={showAlert.severity} sx={{ width: '100%' }}>
            {showAlert.message}
          </Alert>
        </Snackbar>
      )}

      <Typography variant="h4" gutterBottom>
        Crear Nuevo Proyecto
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Campo para Nombre del Proyecto */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre del Proyecto"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Descripción */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
              multiline
              rows={4}
            />
          </Grid>

          {/* Campo para Fecha de Inicio */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha de Inicio"
              type="date"
              value={fechaInicio}
              onChange={(e) => setFechaInicio(e.target.value)}
              required
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          {/* Campo para Fecha de Finalización */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha de Finalización"
              type="date"
              value={fechaFinalizacion}
              onChange={(e) => setFechaFinalizacion(e.target.value)}
              required
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          {/* Campo para Estado */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Estado"
              value={estado}
              onChange={(e) => setEstado(e.target.value)}
              required
              select
            >
              <option value="En progreso">En progreso</option>
              <option value="Finalizado">Finalizado</option>
              <option value="Suspendido">Suspendido</option>
            </TextField>
          </Grid>

          {/* Campo para Presupuesto */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Presupuesto"
              value={presupuesto}
              onChange={(e) => setPresupuesto(e.target.value)}
              type="number"
              required
            />
          </Grid>

          {/* Campo para Responsable (ID) */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Responsable (ID)"
              value={idResponsable}
              onChange={(e) => setIdResponsable(e.target.value)}
              type="number"
              required
            />
          </Grid>

          {/* Botón para enviar el formulario */}
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Crear Proyecto
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default CrearProyecto;
