import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import DashboardLayoutNavigationLinks from './components/DashboardPageLayout';
import Login from './pages/Login';
import Usuario from './pages/Seguridad/usuario';
import { isAuthenticated } from './utils/auth'; // Verifica la autenticación del usuario

function App() {
    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route path="/login" element={<Login />} />

                {/* Rutas públicas o protegidas */}
                <Route path="/seguridad/*" element={
                    isAuthenticated() ? <DashboardLayoutNavigationLinks /> : <Navigate to="/login" />
                }>
                    {/* Rutas internas de seguridad */}
                    <Route path="usuarios" element={<Usuario />} />
                    {/* Otras rutas internas pueden ir aquí */}
                </Route>

                {/* Ruta para redirigir al login si no se encuentra la ruta */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
