import React, { useEffect, useState, useCallback } from 'react';
import { createTheme } from '@mui/material/styles';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { Routes, Route, useNavigate, Navigate } from 'react-router-dom';
import axios from 'axios';
import {jwtDecode} from 'jwt-decode';
import Usuario from '../pages/Seguridad/usuario';
import Roles from '../pages/Seguridad/Roles';
import CrearUsuario from '../pages/Seguridad/CrearUsuario'; 
import EditarUsuario from '../pages/Seguridad/EditarUsuario';
import CrearRoles from '../pages/Seguridad/CrearRoles'; 
import EditarRol from '../pages/Seguridad/EditarRol'; 
import CrearPermisos from '../pages/Seguridad/CrearPermisos'; 

// Crear el tema para la interfaz de usuario
const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const [permissions, setPermissions] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    // Función para obtener el menú desde la API
    const fetchMenus = useCallback(async () => {
        if (!token) return;

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            // Petición a la API para obtener el menú y permisos
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            setMenuItems(response.data);
            // Ajustar para que los permisos vengan de la respuesta o del token
            const userPermissions = decoded.user.permissions || response.data.map(item => item.MenuURL);
            setPermissions(userPermissions); // Aquí almacenamos los permisos
            console.log('Permisos obtenidos:', userPermissions); // Para depuración
        } catch (error) {
            console.error('Error al obtener los menús:', error);
        }
    }, [token]);

    // Efecto para verificar autenticación y cargar los menús
    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    // Función que maneja el clic en el menú para redirigir
    const handleMenuClick = (url) => {
        if (url) {
            navigate(`/${url}`);
        }
    };

    // Organizar el menú
    const navigation = menuItems.map((item) => {
        if (item.ParentMenuID === 0 && item.VisibleUsuario === 1) {
            return {
                segment: item.MenuURL,
                title: item.DisplayName,
                children: menuItems
                    .filter((subItem) => subItem.ParentMenuID === item.MenuID)
                    .map((subItem) => ({
                        segment: subItem.MenuURL,
                        title: subItem.DisplayName,
                        onClick: () => handleMenuClick(subItem.MenuURL),
                    })),
                onClick: () => handleMenuClick(item.MenuURL),
            };
        }
        return null;
    }).filter(Boolean);

  // Función para verificar si el usuario tiene permiso para acceder a la URL
const checkPermission = (url) => {
    // Itera sobre los permisos y verifica si alguna de las rutas coincide con el prefijo
    const hasPermission = permissions.some(permission => url.startsWith(permission));
    console.log(`Verificación de permiso para ${url}: ${hasPermission}`);
    return hasPermission;
};



    // Componente de ruta protegida
    const ProtectedRoute = ({ element: Component, url, ...rest }) => {
        return checkPermission(url) ? (
            <Component {...rest} />
        ) : (
            <Navigate to="/login" />
        );
    };

    return (
        <AppProvider navigation={navigation} theme={demoTheme}>
            <DashboardLayout>
                <Routes>
                    <Route path="seguridad/usuarios" element={<ProtectedRoute element={Usuario} url="seguridad/usuarios" />} />
                    <Route path="seguridad/crear-usuario" element={<ProtectedRoute element={CrearUsuario} url="seguridad/crear-usuario" />} />
                    <Route path="seguridad/editar-usuario/:id" element={<ProtectedRoute element={EditarUsuario} url="seguridad/editar-usuario/:id" />} />
                    <Route path="seguridad/roles" element={<ProtectedRoute element={Roles} url="seguridad/roles" />} />
                    <Route path="seguridad/crear-roles" element={<ProtectedRoute element={CrearRoles} url="seguridad/crear-roles" />} />
                    <Route path="seguridad/editar-roles/:id" element={<ProtectedRoute element={EditarRol} url="seguridad/editar-roles/:id" />} />
                    <Route path="seguridad/crear-permisos/:id" element={<ProtectedRoute element={CrearPermisos} url="seguridad/crear-permisos/:id" />} />
                </Routes>
            </DashboardLayout>
        </AppProvider>
    );
}
