// DashboardLayoutNavigationLinks.js
import React, { useEffect, useState, useCallback } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import SecurityIcon from '@mui/icons-material/Security';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, Routes, Route, useLocation } from 'react-router-dom';
import axios from 'axios';
import Usuario from '../pages/Seguridad/usuario';
import { jwtDecode } from 'jwt-decode';

// Definición del tema
const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchMenus = useCallback(async () => {
        if (!isAuthenticated) return;

        try {
            const decoded = jwtDecode(token);
            const username = decoded?.user?.username;

            if (!username) {
                navigate('/login');
                return;
            }

            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
            navigate('/login');
        }
    }, [isAuthenticated, token, navigate]);

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    // Definición de la navegación
    const navigation = menuItems.map((item) => {
        if (item.ParentMenuID === 0) {
            return {
                segment: item.DisplayName,
                title: item.DisplayName,
                icon: iconMap[item.DisplayName] || <DescriptionIcon />,
                children: menuItems
                    .filter((subItem) => subItem.ParentMenuID === item.MenuID)
                    .map((subItem) => ({
                        segment: subItem.DisplayName,
                        title: subItem.DisplayName,
                        icon: iconMap[subItem.DisplayName] || <DescriptionIcon />,
                        onClick: () => handleMenuClick(subItem.MenuURL),
                    })),
            };
        }
        return null;
    }).filter(Boolean);

    return (
        <AppProvider navigation={navigation} theme={demoTheme}>
            <DashboardLayout>
                <Box sx={{ flexGrow: 1 }}>
                    <Routes>
                        {menuItems.map((item) => (
                            <Route
                                key={item.MenuID}
                                path={item.MenuURL}
                                element={item.MenuURL === '/Seguridad/Usuarios' ? <Usuario /> : <Welcome />}
                            />
                        ))}
                        <Route path="*" element={<Welcome />} />
                    </Routes>
                </Box>
            </DashboardLayout>
        </AppProvider>
    );
}

function Welcome() {
    return (
        <Box sx={{ py: 35, display: 'flex', flexDirection: 'column', alignItems: 'center', textAlign: 'center' }}>
            <Typography variant="h4">Bienvenido al Gestor de Control de Calidad!</Typography>
        </Box>
    );
}
