import React, { useEffect, useState, useCallback } from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import SecurityIcon from '@mui/icons-material/Security';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import Usuario from '../pages/Seguridad/usuario'; // Verifica que la ruta sea correcta
import { jwtDecode } from 'jwt-decode';

const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

const iconMap = {
    Seguridad: <SecurityIcon />,
    Roles: <PersonIcon />,
    Usuarios: <DescriptionIcon />,
    Permisos: <LockIcon />,
};

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchMenus = useCallback(async () => {
        if (!token) return;

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            console.log('API Response:', response.data); // Log para verificar la respuesta
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    }, [token]);

    useEffect(() => {
        if (!isAuthenticated) {
            console.log('Usuario no autenticado, redirigiendo a login.'); // Para depuración
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    // Ajuste en la lógica de navegación
    const handleMenuClick = (url) => {
        if (url) {
            console.log('Navegando a:', url); // Para depuración
            navigate(`/dashboard/${url.trim()}`); // Ruta correcta con /dashboard
        }
    };

    const navigation = menuItems.map((item) => {
        if (item.ParentMenuID === 0) {
            return {
                segment: item.DisplayName,
                title: item.DisplayName,
                icon: iconMap[item.DisplayName] || <DescriptionIcon />,
                children: menuItems
                    .filter((subItem) => subItem.ParentMenuID === item.MenuID)
                    .map((subItem) => ({
                        segment: subItem.DisplayName,
                        title: subItem.DisplayName,
                        icon: iconMap[subItem.DisplayName] || <DescriptionIcon />,
                        onClick: () => handleMenuClick(subItem.MenuURL.trim()), // Asegúrate de usar la ruta correcta
                    })),
                onClick: () => handleMenuClick(item.MenuURL.trim()), // Asegúrate de usar la ruta correcta
            };
        }
        return null;
    }).filter(Boolean);

    return (
        <AppProvider navigation={navigation} theme={demoTheme}>
            <DashboardLayout>
                <Routes>
                    {/* Asegúrate de que las rutas sean relativas a /dashboard */}
                    {menuItems.map((item) => (
                        <Route path="/dashboard/seguridad/usuarios" element={<Usuario />} />

                    ))}
                    <Route path="/dashboard" element={<Welcome />} />
                    <Route path="*" element={<Welcome />} />
                </Routes>
            </DashboardLayout>
        </AppProvider>
    );
}

function Welcome() {
    return (
        <Box
            sx={{
                py: 35,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                textAlign: 'center',
            }}
        >
            <Typography variant="h4">Bienvenido al Gestor de Control de Calidad!</Typography>
        </Box>
    );
}
