import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom'; // Para obtener el id del rol
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Función para verificar autenticación

const CrearPermisos = () => {
  const [showAlert, setShowAlert] = useState(null); // Alerta
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el id del rol desde la URL

  // Estados para los campos del formulario de rol (solo lectura)
  const [rolName, setRolName] = useState('');
  const [descripcion, setDescripcion] = useState('');

  // Estados para los campos del formulario de menú
  const [displayName, setDisplayName] = useState('');
  const [parentMenuID, setParentMenuID] = useState('');
  const [orderNumber, setOrderNumber] = useState('');
  const [menuURL, setMenuURL] = useState('');
  const [menuIcon, setMenuIcon] = useState('');

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    } else {
      // Cargar los datos del rol si estamos editando
      if (id) {
        fetchRoleData(id);
      }
    }
  }, [id, navigate]);

  // Función para cargar los datos del rol
  const fetchRoleData = async (roleId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };

      const response = await axios.get(`http://localhost:5000/api/roles/roledit/${roleId}`, config);
      const roleData = response.data;

      // Llenar los campos de rol con los datos obtenidos
      setRolName(roleData.NOMBRE);
      setDescripcion(roleData.DESCRIPCION);
    } catch (error) {
      console.error('Error al cargar el rol:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los datos del rol.',
      });
    }
  };

  // Función para manejar el envío del formulario de creación de menú
  const handleMenuSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples
    if (!displayName || !parentMenuID || !orderNumber || !menuURL || !menuIcon) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos del menú son obligatorios.',
      });
      return;
    }

    const newMenu = { displayName, parentMenuID, orderNumber, menuURL, menuIcon };

    try {
      const token = localStorage.getItem('token');

      // Verificar si el token existe
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT en la cabecera
        },
      };

      // Hacer la solicitud POST para crear el menú
      const response = await axios.post('http://localhost:5000/api/menu', newMenu, config);

      // Verificar la respuesta de la API
      if (response.data.msg === 'Menú creado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg, // Mostrar el mensaje de éxito
        });

        // Limpiar los campos del formulario si el menú se creó correctamente
        setDisplayName('');
        setParentMenuID('');
        setOrderNumber('');
        setMenuURL('');
        setMenuIcon('');

        // Redirigir a la lista de menús después de 2 segundos
        setTimeout(() => {
          navigate('/menus');
        }, 2000);
      } else {
        setShowAlert({
          severity: 'error',
          message: response.data.msg || 'Error al crear el menú.',
        });
      }
    } catch (error) {
      console.error('Error al crear el menú:', error.response || error);

      // Si hay un error específico de la API
      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el menú. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}



      {/* Mostrar los datos del rol en un estilo de solo lectura */}
      <div style={{ marginBottom: '20px', marginTop: '20px' }}>
        <Typography variant="h6" gutterBottom>
          Datos del Rol:
        </Typography>

        <Typography variant="h4" gutterBottom>
        Editar Rol
      </Typography>
      
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre del Rol"
              value={rolName}
              InputProps={{
                readOnly: true,
                style: { backgroundColor: '#f5f5f5' }, // Fondo gris claro para hacerlo más visible como solo lectura
              }}
              variant="outlined"
              size="small"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              InputProps={{
                readOnly: true,
                style: { backgroundColor: '#f5f5f5' }, // Fondo gris claro
              }}
              variant="outlined"
              size="small"
            />
          </Grid>
        </Grid>
      </div>

      <Typography variant="h5" gutterBottom>
        Crear Menú
      </Typography>

      {/* Formulario para crear un nuevo menú */}
      <form onSubmit={handleMenuSubmit}>
        <Grid container spacing={2}>
          {/* Campo para Display Name */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Display Name"
              value={displayName}
              onChange={(e) => setDisplayName(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Parent Menu ID */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Parent Menu ID"
              value={parentMenuID}
              onChange={(e) => setParentMenuID(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Order Number */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Order Number"
              type="number"
              value={orderNumber}
              onChange={(e) => setOrderNumber(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Menu URL */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Menu URL"
              value={menuURL}
              onChange={(e) => setMenuURL(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Menu Icon */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Menu Icon"
              value={menuIcon}
              onChange={(e) => setMenuIcon(e.target.value)}
              required
            />
          </Grid>

          {/* Botón para enviar el formulario */}
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Crear Menú
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default CrearPermisos;
