import React, { useState } from 'react';
import { TextField, Button, MenuItem, FormControl, InputLabel, Select, Grid, Typography } from '@mui/material';

const FormMenu = () => {
  // Estado para manejar los valores del formulario
  const [formData, setFormData] = useState({
    displayName: '',
    parentMenuID: '',
    orderNumber: '',
    menuURL: '',
    menuIcon: '',
  });

  // Función para manejar el cambio de los inputs
  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  // Función para manejar el envío del formulario
  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí puedes enviar los datos del formulario a tu API o manejarlos como sea necesario
    console.log(formData);
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <Typography variant="h4" gutterBottom>
        Formulario de Menú
      </Typography>
      <form onSubmit={handleSubmit}>
        <Grid container spacing={3}>
          {/* Display Name */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Display Name"
              name="displayName"
              value={formData.displayName}
              onChange={handleChange}
              required
            />
          </Grid>

          {/* Parent Menu ID */}
          <Grid item xs={12}>
            <FormControl fullWidth>
              <InputLabel>Parent Menu ID</InputLabel>
              <Select
                label="Parent Menu ID"
                name="parentMenuID"
                value={formData.parentMenuID}
                onChange={handleChange}
                required
              >
                <MenuItem value={1}>Menú 1</MenuItem>
                <MenuItem value={2}>Menú 2</MenuItem>
                <MenuItem value={3}>Menú 3</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          {/* Order Number */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Order Number"
              name="orderNumber"
              type="number"
              value={formData.orderNumber}
              onChange={handleChange}
              required
            />
          </Grid>

          {/* Menu URL */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Menu URL"
              name="menuURL"
              value={formData.menuURL}
              onChange={handleChange}
              required
            />
          </Grid>

          {/* Menu Icon */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Menu Icon"
              name="menuIcon"
              value={formData.menuIcon}
              onChange={handleChange}
              required
            />
          </Grid>

          {/* Submit Button */}
          <Grid item xs={12}>
            <Button variant="contained" color="primary" type="submit" fullWidth>
              Enviar
            </Button>
          </Grid>
        </Grid>
      </form>
    </div>
  );
};

export default FormMenu;
