import React, { useState, useEffect } from 'react';
import { Box, Grid, Typography, Checkbox, FormControlLabel, TextField } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const CrearPermisos = () => {
  const [showAlert, setShowAlert] = useState(null);
  const navigate = useNavigate();
  const { id } = useParams();
  
  const [rolName, setRolName] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [menus, setMenus] = useState([]);
  const [permissions, setPermissions] = useState({});

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    } else {
      if (id) {
        fetchRoleData(id);
      }
      fetchMenus();
    }
  }, [id, navigate]);

  const fetchRoleData = async (roleId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({ severity: 'error', message: 'No se encontró el token de autenticación.' });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get(`http://localhost:5000/api/roles/roledit/${roleId}`, config);
      const roleData = response.data;
      setRolName(roleData.NOMBRE);
      setDescripcion(roleData.DESCRIPCION);
    } catch (error) {
      console.error('Error al cargar el rol:', error.response || error);
      setShowAlert({ severity: 'error', message: 'Hubo un error al cargar los datos del rol.' });
    }
  };

  const fetchMenus = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({ severity: 'error', message: 'No se encontró el token de autenticación.' });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get('http://localhost:5000/api/navigation/menu', config);
      
      // Clasificar y ordenar los menús como en la consulta SQL
      const sortedMenus = response.data.sort((a, b) => {
        const getType = (menu) => {
          if (menu.ParentMenuID === 0) return 'Menu';
          if (menu.Accion === 1 || menu.Accion === 2) return 'Sub-submenu';
          return 'Submenu';
        };

        const typeA = getType(a);
        const typeB = getType(b);

        if (typeA !== typeB) {
          return typeA.localeCompare(typeB);
        }
        
        if (a.ParentMenuID === 0) return a.MenuID - b.MenuID;
        if (a.ParentMenuID !== b.ParentMenuID) return a.ParentMenuID - b.ParentMenuID;

        return a.MenuID - b.MenuID || a.Accion - b.Accion;
      });

      setMenus(sortedMenus);
    } catch (error) {
      console.error('Error al cargar los menús:', error.response || error);
      setShowAlert({ severity: 'error', message: 'Hubo un error al cargar los menús.' });
    }
  };

  const handlePermissionChange = (menuId, action) => {
    setPermissions(prevState => {
      const updatedPermissions = { ...prevState };
      if (!updatedPermissions[menuId]) {
        updatedPermissions[menuId] = { view: false, edit: false, delete: false };
      }
      updatedPermissions[menuId][action] = !updatedPermissions[menuId][action];
      return updatedPermissions;
    });
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" textAlign={'center'} gutterBottom>
        Asignación de permisos
      </Typography>

      <div style={{ marginBottom: '20px', marginTop: '20px' }}>
        <Typography variant="h6" gutterBottom>
          Datos del Rol:
        </Typography>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre del Rol"
              value={rolName}
              InputProps={{ readOnly: true, style: { backgroundColor: '#f5f5f5' }}}
              variant="outlined"
              size="small"
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              InputProps={{ readOnly: true, style: { backgroundColor: '#f5f5f5' }}}
              variant="outlined"
              size="small"
            />
          </Grid>
        </Grid>
      </div>

      <Typography variant="h5" gutterBottom>
        Menús Principales y Submenús
      </Typography>
      {menus.length === 0 ? (
        <Typography variant="body1">No hay menús disponibles.</Typography>
      ) : (
        <Grid container spacing={2}>
          {menus.map((menu) => (
            <Grid item xs={12} sm={6} key={menu._id}>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', border: '1px solid #ccc', padding: 2, borderRadius: 1 }}>
                <Typography variant="h6">{menu.DisplayName}</Typography>
                <div>
                  <FormControlLabel
                    control={
                      <Checkbox
                        checked={permissions[menu._id]?.view || false}
                        onChange={() => handlePermissionChange(menu._id, 'view')}
                        color="primary"
                      />
                    }
                    label="Mostrar"
                  />
                </div>
              </Box>
            </Grid>
          ))}
        </Grid>
      )}
    </Box>
  );
};

export default CrearPermisos;
