import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import DashboardLayoutNavigationLinks from './components/DashboardPageLayout';
import Login from './pages/Login';
import Usuario from './pages/Seguridad/usuario';
import { getAuthToken, isAuthenticated, getUserFromToken } from './utils/auth'; // Importamos las funciones de auth.js

function App() {
    const [isAuthenticatedState, setIsAuthenticated] = useState(false);
    const [userName, setUserName] = useState('');

    // Verificar si el usuario está autenticado y obtener el nombre de usuario
    useEffect(() => {
        const token = getAuthToken(); // Obtenemos el token desde localStorage
        if (isAuthenticated()) {
            setIsAuthenticated(true);
            setUserName(getUserFromToken()); // Obtenemos el nombre de usuario desde el token
        } else {
            setIsAuthenticated(false);
        }
    }, []);

    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route path="/login" element={<Login />} />

                {/* Si el usuario no está autenticado, redirigir al login */}
                <Route
                    path="/dashboard/*"
                    element={
                        isAuthenticatedState ? (
                            <DashboardLayoutNavigationLinks userName={userName} /> // Pasamos el nombre de usuario
                        ) : (
                            <Navigate to="/login" />
                        )
                    }
                >
                    {/* Rutas internas del Dashboard */}
                    <Route path="usuarios" element={<Usuario />} />
                    {/* Puedes agregar más rutas internas aquí */}
                    {/* <Route path="roles" element={<Roles />} /> */}
                    {/* <Route path="permisos" element={<Permisos />} /> */}
                </Route>

                {/* Redirigir a login si la ruta no existe */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
