import React, { useState } from 'react';
import { Container, TextField, Button, Typography, Box } from '@mui/material';
import { styled } from '@mui/system';
import logo from '../assets/LogoPrincipal.png'; // Asegúrate de que la ruta sea correcta

// Estilo para el logo circular
const CircularLogo = styled('img')({
  width: '350px', // Ajusta el tamaño según sea necesario
  height: '200px',
  borderRadius: '50%', // Esto hace que el logo sea circular
  display: 'block',
  margin: '0 auto', // Centra el logo
});

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [errors, setErrors] = useState({});
  
    const handleSubmit = (event) => {
      event.preventDefault();
      
      // Validación básica
      const newErrors = {};
      if (!usuario) newErrors.email = 'El correo electrónico es requerido';
      if (!password) newErrors.password = 'La contraseña es requerida';
      
      if (Object.keys(newErrors).length > 0) {
        setErrors(newErrors);
        return; // Detenemos el envío del formulario si hay errores
      }
  
      // Aquí puedes agregar la lógica para manejar el inicio de sesión
      console.log("Formulario enviado", { email, password });
      
      // Limpiar los errores después de enviar
      setErrors({});
    };
  
    return (
      <Container maxWidth="xs" sx={{ mt: 8 }}>
        <CircularLogo src={logo} alt="Logo" />
        <Typography variant="h4" align="center" gutterBottom>
          Iniciar Sesión
        </Typography>
        <Box component="form" noValidate onSubmit={handleSubmit} sx={{ mt: 1 }}>
          <TextField
            required
            fullWidth
            label="Usuario"
            margin="normal"
            value={text}
            onChange={(e) => setEmail(e.target.value)}
            error={Boolean(errors.email)}
            helperText={errors.email}
          />
          <TextField
            required
            fullWidth
            label="Contraseña"
            type="password"
            margin="normal"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            error={Boolean(errors.password)}
            helperText={errors.password}
          />
          <Button type="submit" variant="contained" color="primary" fullWidth>
            Iniciar Sesión
          </Button>
        </Box>
      </Container>
    );
  };
  
  export default Login;
