// CrearUsuario.js
import React from 'react';
import { Box, Typography, TextField, Button } from '@mui/material';

const CrearUsuario = () => {
    const handleSubmit = (e) => {
        e.preventDefault();
        // Lógica para manejar el envío del formulario
    };

    return (
        <Box
            sx={{
                padding: 3,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                textAlign: 'center',
                maxWidth: 600,
                margin: 'auto',
            }}
        >
            <Typography variant="h4" gutterBottom>
                Crear Usuario
            </Typography>
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Nombre"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                />
                <TextField
                    label="Correo Electrónico"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                />
                <TextField
                    label="Contraseña"
                    type="password"
                    variant="outlined"
                    fullWidth
                    margin="normal"
                />
                <Button type="submit" variant="contained" color="primary">
                    Crear Usuario
                </Button>
            </form>
        </Box>
    );
};

export default CrearUsuario;
