import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const EditarHito = () => {
  const [showAlert, setShowAlert] = useState(null);
  const navigate = useNavigate();
  const { id } = useParams();

  // Estados para los campos del formulario
  const [titulo, setTitulo] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fecha, setFecha] = useState('');
  const [hitos, setHitos] = useState([]); // Lista de hitos para la tabla

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    } else {
      if (id) {
        fetchHitoData(id);
        fetchHitos(); // Cargar la lista de hitos
      }
    }
  }, [id, navigate]);

  const fetchHitoData = async (hitoId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get(`http://localhost:5000/api/hitos/hitoedit/${hitoId}`, config);

      const hitoData = response.data;
      setTitulo(hitoData.TITULO);
      setDescripcion(hitoData.DESCRIPCION);
      setFecha(hitoData.FECHA);
    } catch (error) {
      console.error('Error al cargar el hito:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los datos del hito.',
      });
    }
  };

  const fetchHitos = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get('http://localhost:5000/api/hitos', config);

      setHitos(response.data);
    } catch (error) {
      console.error('Error al cargar los hitos:', error.response || error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!titulo || !descripcion || !fecha) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const updatedHito = { titulo, descripcion, fecha };

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.put(`http://localhost:5000/api/hitos/hito/${id}`, updatedHito, config);

      if (response.data.msg === 'Hito actualizado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg,
        });

        setTitulo('');
        setDescripcion('');
        setFecha('');

        setTimeout(() => {
          navigate('/proyectos/hitos');
        }, 2000);
      } else {
        setShowAlert({
          severity: 'error',
          message: response.data.msg || 'Error al actualizar el hito.',
        });
      }
    } catch (error) {
      console.error('Error al actualizar el hito:', error.response || error);

      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al actualizar el hito. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom>
        Editar Hito
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Título del Hito"
              value={titulo}
              onChange={(e) => setTitulo(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha"
              type="date"
              InputLabelProps={{ shrink: true }}
              value={fecha}
              onChange={(e) => setFecha(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={6}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Actualizar Hito
            </Button>
          </Grid>
        </Grid>
      </form>

      <Typography variant="h5" sx={{ mt: 4, mb: 2 }}>
        Lista de Hitos
      </Typography>
      <TableContainer component={Paper}>
        <Table>
          <TableHead>
            <TableRow>
              <TableCell>Título</TableCell>
              <TableCell>Descripción</TableCell>
              <TableCell>Fecha</TableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {hitos.map((hito) => (
              <TableRow key={hito.id}>
                <TableCell>{hito.TITULO}</TableCell>
                <TableCell>{hito.DESCRIPCION}</TableCell>
                <TableCell>{hito.FECHA}</TableCell>
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </Box>
  );
};

export default EditarHito;
