import React, { useState, useEffect, useCallback } from 'react';
import {
    Box,
    TextField,
    Button,
    Grid,
    Typography,
    Paper,
    Table,
    TableBody,
    TableCell,
    TableContainer,
    TableHead,
    TableRow,
    MenuItem,
    Select,
    InputLabel,
    FormControl,
    CircularProgress,
    IconButton,
    Alert,
} from '@mui/material';
import { Edit, Delete } from '@mui/icons-material'; // Íconos de edición y eliminación
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import { isAuthenticated } from '../../utils/auth';
import ConfirmDeleteDialog from '../../';

const CrearHito = () => {
    const { proyectoId } = useParams();
    const [showAlert, setShowAlert] = useState(null);
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFin, setFechaFin] = useState('');
    const [estado, setEstado] = useState('Pendiente');
    const [proyecto, setProyecto] = useState(null);
    const [hitos, setHitos] = useState([]);
    const [loading, setLoading] = useState(true);
    const [openDialog, setOpenDialog] = useState(false); // Estado para abrir/cerrar el diálogo de confirmación
    const [idHitoToDelete, setIdHitoToDelete] = useState(null); // Estado para almacenar el id del hito a eliminar
    const navigate = useNavigate();

    const makeRequest = async (method, url, data = {}) => {
        const token = localStorage.getItem('token');
        if (!token) {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el token de autenticación.',
            });
            return;
        }

        const config = { headers: { 'x-auth-token': token } };
        try {
            const response = await axios[method](url, data, config);
            return response.data;
        } catch (error) {
            setShowAlert({
                severity: 'error',
                message: error.response?.data?.msg || error.message || 'Hubo un error inesperado con la solicitud.',
            });
            return null;
        }
    };

    const fetchProyecto = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        if (data) setProyecto(data);
    }, [proyectoId]);

    const fetchHitos = useCallback(async () => {
        setLoading(true);
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/hito/${proyectoId}`);
        setLoading(false);

        setHitos(data || []);
    }, [proyectoId]);

    const handleEliminarHito = async () => {
        if (idHitoToDelete === null) return;
        try {
            const response = await fetch(`http://localhost:5000/api/hitos/EliminarHito/${idHitoToDelete}`, {
                method: 'DELETE',
            });
            if (response.ok) {
                setShowAlert({ severity: 'success', message: 'Hito eliminado exitosamente' });
                fetchHitos(); // Vuelve a cargar los hitos después de la eliminación
                setOpenDialog(false); // Cierra el diálogo de confirmación
            } else {
                setShowAlert({ severity: 'error', message: 'Error al eliminar el hito' });
            }
        } catch (error) {
            console.error('Error al eliminar el hito:', error);
            setShowAlert({ severity: 'error', message: 'Error al eliminar el hito' });
        }
    };

    const handleDelete = (id_hito) => {
        setIdHitoToDelete(id_hito); // Guarda el id del hito a eliminar
        setOpenDialog(true); // Abre el diálogo de confirmación
    };

    const handleCloseDialog = () => {
        setOpenDialog(false); // Cierra el diálogo de confirmación sin eliminar
    };

    const handleEdit = (id_hito) => {
        navigate(`/gestiones/editar-hitos/${id_hito}`);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        if (!titulo || !descripcion || !fechaInicio || !fechaFin) {
            setShowAlert({
                severity: 'error',
                message: 'Todos los campos son obligatorios.',
            });
            return;
        }

        const newHito = { titulo, descripcion, fechaInicio, fechaFin, estado, proyectoId };
        const response = await makeRequest('post', 'http://localhost:5000/api/hitos/register', newHito);
        if (response) {
            setShowAlert({
                severity: 'success',
                message: 'Hito creado exitosamente.',
            });
            setTitulo('');
            setDescripcion('');
            setFechaInicio('');
            setFechaFin('');
            setEstado('Pendiente');
            fetchHitos();
        }
    };

    const formatDate = (dateString) => {
        const date = new Date(dateString);
        return `${date.getDate()}/${date.getMonth() + 1}/${date.getFullYear()}`;
    };

    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login');
        } else {
            fetchProyecto();
            fetchHitos();
        }
    }, [navigate, fetchProyecto, fetchHitos]);

    useEffect(() => {
        if (showAlert) {
            const timer = setTimeout(() => setShowAlert(null), 3000); // Ocultar alerta después de 3 segundos
            return () => clearTimeout(timer); // Limpiar temporizador al desmontar
        }
    }, [showAlert]);

    return (
        <Box sx={{ padding: 5 }}>
            {showAlert && (
                <Alert severity={showAlert.severity} sx={{ mb: 4 }}>
                    {showAlert.message}
                </Alert>
            )}

            <Typography variant="h5" gutterBottom>
                {proyecto ? `Proyecto: ${proyecto.nombre}` : <CircularProgress size={24} />}
            </Typography>

            <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Título del Hito"
                            value={titulo}
                            onChange={(e) => setTitulo(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Descripción"
                            value={descripcion}
                            onChange={(e) => setDescripcion(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de inicio"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaInicio}
                            onChange={(e) => setFechaInicio(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <TextField
                            fullWidth
                            label="Fecha de finalización"
                            type="date"
                            InputLabelProps={{ shrink: true }}
                            value={fechaFin}
                            onChange={(e) => setFechaFin(e.target.value)}
                            required
                        />
                    </Grid>
                    <Grid item xs={12} sm={6}>
                        <FormControl fullWidth>
                            <InputLabel>Estado</InputLabel>
                            <Select value={estado} onChange={(e) => setEstado(e.target.value)} label="Estado">
                                <MenuItem value="Pendiente">Pendiente</MenuItem>
                                <MenuItem value="En Proceso">En Proceso</MenuItem>
                                <MenuItem value="Finalizado">Finalizado</MenuItem>
                            </Select>
                        </FormControl>
                    </Grid>
                    <Grid item xs={12}>
                        <Button type="submit" variant="contained" color="primary" fullWidth>
                            Crear Hito
                        </Button>
                    </Grid>
                </Grid>
            </form>

            <Paper sx={{ mt: 4, p: 3 }}>
                <Typography variant="h6" gutterBottom>Hitos del Proyecto</Typography>
                <TableContainer>
                    <Table>
                        <TableHead>
                            <TableRow>
                                <TableCell>Título</TableCell>
                                <TableCell>Descripción</TableCell>
                                <TableCell>Fecha Inicio</TableCell>
                                <TableCell>Fecha Fin</TableCell>
                                <TableCell>Estado</TableCell>
                                <TableCell>Acciones</TableCell>
                            </TableRow>
                        </TableHead>
                        <TableBody>
                            {hitos.map((hito) => (
                                <TableRow key={hito._id}>
                                    <TableCell>{hito.titulo}</TableCell>
                                    <TableCell>{hito.descripcion}</TableCell>
                                    <TableCell>{formatDate(hito.fechaInicio)}</TableCell>
                                    <TableCell>{formatDate(hito.fechaFin)}</TableCell>
                                    <TableCell>{hito.estado}</TableCell>
                                    <TableCell>
                                        <IconButton onClick={() => handleEdit(hito._id)} color="primary">
                                            <Edit />
                                        </IconButton>
                                        <IconButton onClick={() => handleDelete(hito._id)} color="secondary">
                                            <Delete />
                                        </IconButton>
                                    </TableCell>
                                </TableRow>
                            ))}
                        </TableBody>
                    </Table>
                </TableContainer>
            </Paper>

            {/* Dialogo de confirmación para eliminar el hito */}
            <ConfirmDeleteDialog
                open={openDialog}
                onClose={handleCloseDialog}
                onConfirm={handleEliminarHito}
            />
        </Box>
    );
};

export default CrearHito;
