import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const CrearHito = () => {
  const { proyectoId } = useParams(); // Obtener el id del proyecto desde la URL
  const [showAlert, setShowAlert] = useState(null);
  const [titulo, setTitulo] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fecha, setFecha] = useState('');
  const [proyecto, setProyecto] = useState(null); // Estado para el proyecto
  const [hitos, setHitos] = useState([]); // Estado para los hitos del proyecto
  const navigate = useNavigate();

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login');
    } else {
      fetchProyecto(); // Cargar el proyecto específico usando el ID de la URL
      fetchHitos(); // Cargar los hitos del proyecto
    }
  }, [navigate, proyectoId]);

  const fetchProyecto = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get(`http://localhost:5000/api/proyectos/${proyectoId}`, config); // Ajusta la URL para recuperar un proyecto por ID

      if (response.data) {
        setProyecto(response.data); // Guardar el proyecto específico
      } else {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el proyecto.',
        });
      }
    } catch (error) {
      console.error('Error al cargar el proyecto:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar el proyecto.',
      });
    }
  };

  const fetchHitos = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.get(`http://localhost:5000/api/hitos/proyecto/${proyectoId}`, config);

      if (response.data) {
        setHitos(response.data); // Guardar los hitos del proyecto
      } else {
        setHitos([]);
      }
    } catch (error) {
      console.error('Error al cargar los hitos:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los hitos del proyecto.',
      });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!titulo || !descripcion || !fecha || !proyectoId) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const newHito = { titulo, descripcion, fecha, proyectoId };

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = { headers: { 'x-auth-token': token } };
      const response = await axios.post('http://localhost:5000/api/hitos', newHito, config);

      if (response.data.msg === 'Hito creado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg,
        });

        setTitulo('');
        setDescripcion('');
        setFecha('');

        fetchHitos(); // Refrescar la lista de hitos

        setTimeout(() => {
          navigate(`/proyectos/${proyectoId}/hitos`);
        }, 2000);
      } else {
        setShowAlert({
          severity: 'error',
          message: response.data.msg || 'Error al crear el hito.',
        });
      }
    } catch (error) {
      console.error('Error al crear el hito:', error.response || error);

      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el hito. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h5" gutterBottom>
        Crear Nuevo Hito para el Proyecto: {proyecto ? proyecto.nombre : 'Cargando...'}
      </Typography>

      {/* Formulario de creación de hito */}
      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Título del Hito"
              value={titulo}
              onChange={(e) => setTitulo(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha de inicio"
              type="date"
              InputLabelProps={{ shrink: true }}
              value={fecha}
              onChange={(e) => setFecha(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha de finalización"
              type="date"
              InputLabelProps={{ shrink: true }}
              value={fecha}
              onChange={(e) => setFecha(e.target.value)}
              required
            />
          </Grid>
          <Grid item xs={6}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Crear Hito
            </Button>
          </Grid>
        </Grid>
      </form>

      {/* Mostrar la tabla de hitos del proyecto */}
      <Paper sx={{ mt: 4, p: 3 }}>
        <Typography variant="h6" gutterBottom>Hitos del Proyecto</Typography>
        <TableContainer>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell><strong>Título</strong></TableCell>
                <TableCell><strong>Descripción</strong></TableCell>
                <TableCell><strong>Fecha</strong></TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {hitos.length > 0 ? (
                hitos.map((hito) => (
                  <TableRow key={hito._id}>
                    <TableCell>{hito.titulo}</TableCell>
                    <TableCell>{hito.descripcion}</TableCell>
                    <TableCell>{new Date(hito.fecha).toLocaleDateString()}</TableCell>
                  </TableRow>
                ))
              ) : (
                <TableRow>
                  <TableCell colSpan={3}>No hay hitos disponibles para este proyecto.</TableCell>
                </TableRow>
              )}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
    </Box>
  );
};

export default CrearHito;
