import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Asegúrate de importar esto si usas react-router-dom
import {
  Grid,
  TextField,
  Select,
  MenuItem,
  Button,
  Typography,
  CircularProgress
} from '@mui/material';
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const FormularioHito = () => {
  const [descripcionHito, setDescripcionHito] = useState('');
  const [estadoActualHito, setEstadoActualHito] = useState('');
  const [estadonuevoHito, setEstadonuevoHito] = useState('');
  const [resultadoEsperado, setResultadoEsperado] = useState('');
  const [pruebaRealizada, setPruebaRealizada] = useState('');
  const [criterioAceptacion, setCriterioAceptacion] = useState('');
  const [estadoPrueba, setEstadoPrueba] = useState('');
  const [imagenPrueba, setImagenPrueba] = useState(null);
  const [formularioHabilitado, setFormularioHabilitado] = useState(true);
  const [cargando, setCargando] = useState(true);

  const navigate = useNavigate();


  useEffect(() => {
    // Verificar si el usuario está autenticado
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    } else {
      // Cargar los datos si está autenticado
      const fetchData = async () => {
        try {
          const response = await fetch('API_URL'); // Reemplaza con la URL de tu API
          const data = await response.json();

          setDescripcionHito(data.descripcion);
          setEstadoActualHito(data.estadoActual);
          setResultadoEsperado(data.resultadoEsperado);
          setPruebaRealizada(data.pruebaRealizada);
          setCriterioAceptacion(data.criterioAceptacion);
          setEstadoPrueba(data.estadoPrueba);

          setCargando(false); // Una vez cargados los datos, deshabilitamos el loading
        } catch (error) {
          console.error('Error al recuperar los datos:', error);
          setCargando(false);
        }
      };

      fetchData();
    }
  }, [navigate]);

  const handleImagenChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setImagenPrueba(file);
    }
  };

  const serEstadonuevoHito = (value) => setEstadonuevoHito(value);

  const handleSubmit = (e) => {
    e.preventDefault();
    // Aquí iría la lógica para enviar los datos del formulario, probablemente con un POST hacia tu API.
  };

  if (cargando) {
    return <CircularProgress />;
  }

  return (
    <form onSubmit={handleSubmit}>
      <Grid container spacing={2}>
        <Grid item xs={12} >
          <TextField
            fullWidth
            label="Descripción del Plan de Prueba"
            value={descripcionHito}
            disabled={true}
            variant="outlined"
          />
        </Grid>
        
        <Grid item xs={12} sm={6}>
          <TextField
            fullWidth
            label="Estado actual del hito"
            value={estadoActualHito}
            disabled={true}
            variant="outlined"
          />
        </Grid>

        <Grid item xs={12} sm={6}>
          <Select
            fullWidth
            value={estadonuevoHito}
            onChange={(e) => serEstadonuevoHito(e.target.value)}
            displayEmpty
            disabled={!formularioHabilitado}
          >
            <MenuItem value="" disabled>Cambia el estado del hito ..</MenuItem>
            <MenuItem value="En progreso">En Proceso</MenuItem>
            <MenuItem value="Completado">Finalizado</MenuItem>
          </Select>
        </Grid>

        <Grid item xs={12} sm={12}>
          <TextField
            fullWidth
            label="Resultado Esperado"
            value={resultadoEsperado}
            onChange={(e) => setResultadoEsperado(e.target.value)}
            variant="outlined"
            required
            multiline
            rows={4}
          />
        </Grid>

        <Grid item xs={12} sm={12}>
          <TextField
            fullWidth
            label="Prueba Realizada"
            value={pruebaRealizada}
            onChange={(e) => setPruebaRealizada(e.target.value)}
            variant="outlined"
            required
            multiline
            rows={4}
          />
        </Grid>

        <Grid item xs={12} sm={12}>
          <TextField
            fullWidth
            label="Criterio de aceptación"
            value={criterioAceptacion}
            onChange={(e) => setCriterioAceptacion(e.target.value)}
            variant="outlined"
            required
            multiline
            rows={4}
          />
        </Grid>

        <Grid item xs={12} sm={6}>
          <Select
            fullWidth
            value={estadoPrueba}
            onChange={(e) => setEstadoPrueba(e.target.value)}
            displayEmpty
            disabled={!formularioHabilitado}
            required
          >
            <MenuItem value="" disabled>Selecciona un estado...</MenuItem>
            <MenuItem value="Aprobado">Aprobado</MenuItem>
            <MenuItem value="Rechazada">Rechazada</MenuItem>
            <MenuItem value="Pendiente">Pendiente</MenuItem>
            <MenuItem value="Bloqueado">Bloqueado</MenuItem>
            <MenuItem value="Desconocido">Desconocido</MenuItem>
            <MenuItem value="Requiere más pruebas">Requiere más pruebas</MenuItem>
            <MenuItem value="No aplicable">No aplicable</MenuItem>
          </Select>
        </Grid>

        <Grid item xs={12} sm={6}>
          <Button
            variant="contained"
            component="label"
            fullWidth
          >
            Subir Imagen
            <input
              type="file"
              accept="image/*"
              hidden
              onChange={handleImagenChange}
            />
          </Button>
          {imagenPrueba && (
            <Typography variant="body2" mt={1}>
              Imagen seleccionada: {imagenPrueba.name}
            </Typography>
          )}
        </Grid>

        <Grid item xs={12}>
          <Button
            variant="contained"
            color="primary"
            fullWidth
            type="submit"
          >
            Enviar Plan de Prueba
          </Button>
        </Grid>
      </Grid>
    </form>
  );
};

export default FormularioHito;
