import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Dashboard from './components/Dashboard';
import Login from './pages/Login';

function App() {
    // Simulación de estado de autenticación (cambiar según tu lógica de autenticación)
    const [isAuthenticated, setIsAuthenticated] = useState(false);
    const [usuarioLogueado, setUsuarioLogueado] = useState("nombreDeUsuario"); // Este valor se puede ajustar según el usuario autenticado.

    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route 
                    path="/login" 
                    element={<Login onLogin={() => setIsAuthenticated(true)} />} 
                />

                {/* Ruta protegida para el Dashboard */}
                <Route 
                    path="/dashboard" 
                    element={isAuthenticated ? <Dashboard usuarioLogueado={usuarioLogueado} /> : <Navigate to="/login" />} 
                />

                {/* Redirección a login si la ruta no existe */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
