import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import Usuario from '../pages/Seguridad/usuario'; // Asegúrate de la ruta correcta
import { Routes, Route } from 'react-router-dom';

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchMenus = useCallback(async () => {
        if (!token) return;

        try {
            const response = await axios.get('http://localhost:5000/api/navigation/menu');
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error fetching menus:', error);
        }
    }, [token]);

    useEffect(() => {
        if (!isAuthenticated) {
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    const handleMenuClick = (url) => {
        if (url) {
            navigate(url);
        }
    };

    const navigation = menuItems.map(item => ({
        segment: item.DisplayName,
        title: item.DisplayName,
        icon: <YourIconComponent />, // Asegúrate de tener un ícono correspondiente
        onClick: () => handleMenuClick(item.MenuURL), // Usar MenuURL
    }));

    return (
        <AppProvider navigation={navigation}>
            <DashboardLayout>
                <Routes>
                    <Route path="/usuarios" element={<Usuario />} />
                    {/* Otras rutas dentro del dashboard */}
                </Routes>
            </DashboardLayout>
        </AppProvider>
    );
}
