import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom'; // Para obtener el id desde la URL
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Función para verificar autenticación

const EditarMenu = () => {
  const [showAlert, setShowAlert] = useState(null); // Alerta
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener ID desde la URL

  // Estado para los campos del formulario
  const [displayName, setDisplayName] = useState('');
  const [parentMenuID, setParentMenuID] = useState('');
  const [orderNumber, setOrderNumber] = useState('');
  const [menuURL, setMenuURL] = useState('');
  const [menuIcon, setMenuIcon] = useState('');

  // Estado para los datos históricos
  const [historyData, setHistoryData] = useState(null);

  // Verificar si el usuario está autenticado
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirigir al login si no está autenticado
    } else {
      // Si hay un ID en la URL, cargar los datos del menú
      if (id) {
        fetchMenuData(id);
      }
    }
  }, [id, navigate]);

  // Función para consultar los datos del menú desde la API
  const fetchMenuData = async (menuId) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT
        },
      };

      // Hacer la consulta a la API
      const response = await axios.get(`http://localhost:5000/api/menu/${menuId}`, config);
      const menuData = response.data;

      // Llenar los campos del formulario con los datos recuperados
      setDisplayName(menuData.displayName);
      setParentMenuID(menuData.parentMenuID);
      setOrderNumber(menuData.orderNumber);
      setMenuURL(menuData.menuURL);
      setMenuIcon(menuData.menuIcon);

      // Guardar los datos históricos para mostrar en la parte superior
      setHistoryData(menuData);

    } catch (error) {
      console.error('Error al cargar los datos del menú:', error.response || error);
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al cargar los datos del menú.',
      });
    }
  };

  // Función para manejar el envío del formulario (actualizar el menú)
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones de campos
    if (!displayName || !parentMenuID || !orderNumber || !menuURL || !menuIcon) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const updatedMenu = { displayName, parentMenuID, orderNumber, menuURL, menuIcon };

    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token, // Enviar el token JWT
        },
      };

      // Hacer la solicitud PUT para actualizar el menú
      const response = await axios.put(`http://localhost:5000/api/menu/${id}`, updatedMenu, config);

      // Verificar la respuesta de la API
      if (response.data.msg === 'Menú actualizado correctamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg, // Mensaje de éxito
        });

        // Limpiar los campos del formulario si se actualizó correctamente
        setDisplayName('');
        setParentMenuID('');
        setOrderNumber('');
        setMenuURL('');
        setMenuIcon('');

        // Redirigir después de un corto tiempo
        setTimeout(() => {
          navigate('/menus'); // Redirigir a la página de menús
        }, 2000);
      } else {
        setShowAlert({
          severity: 'error',
          message: response.data.msg || 'Error al actualizar el menú.',
        });
      }
    } catch (error) {
      console.error('Error al actualizar el menú:', error.response || error);

      // Si hay un error específico de la API
      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al actualizar el menú. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom>
        Editar Menú
      </Typography>

      {/* Mostrar los datos históricos del menú */}
      {historyData && (
        <div style={{ marginBottom: '20px' }}>
          <Typography variant="h6" gutterBottom>
            Historial de Datos:
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={12}>
              <Typography><strong>Nombre:</strong> {historyData.displayName}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography><strong>ID del Menú Padre:</strong> {historyData.parentMenuID}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography><strong>Número de Orden:</strong> {historyData.orderNumber}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography><strong>URL del Menú:</strong> {historyData.menuURL}</Typography>
            </Grid>
            <Grid item xs={12}>
              <Typography><strong>Icono del Menú:</strong> {historyData.menuIcon}</Typography>
            </Grid>
          </Grid>
        </div>
      )}

      {/* Formulario para editar el menú */}
      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Display Name"
              value={displayName}
              onChange={(e) => setDisplayName(e.target.value)}
              required
            />
          </Grid>

          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Parent Menu ID"
              value={parentMenuID}
              onChange={(e) => setParentMenuID(e.target.value)}
              required
            />
          </Grid>

          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Order Number"
              type="number"
              value={orderNumber}
              onChange={(e) => setOrderNumber(e.target.value)}
              required
            />
          </Grid>

          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Menu URL"
              value={menuURL}
              onChange={(e) => setMenuURL(e.target.value)}
              required
            />
          </Grid>

          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Menu Icon"
              value={menuIcon}
              onChange={(e) => setMenuIcon(e.target.value)}
              required
            />
          </Grid>

          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Actualizar Menú
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default EditarMenu;
