import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import DashboardLayoutNavigationLinks from './components/DashboardPageLayout';
import Login from './pages/Login'; 
import { isAuthenticated } from './utils/auth'; 

function App() {
    return (
        <Router>
            <Routes>
                {/* Ruta para Login */}
                <Route path="/login" element={<Login />} />

                {/* Rutas del Dashboard */}
                {/* Redirigir al login si no está autenticado */}
                <Route path="/" element={
                    isAuthenticated() ? <DashboardLayoutNavigationLinks /> : <Navigate to="/login" />
                }>
                    {/* Rutas internas del Dashboard */}
                    <Route path="seguridad/usuarios" element={<Usuario />} />
                    {/* <Route path="seguridad/roles" element={<Roles />} />
                    <Route path="seguridad/permisos" element={<Permisos />} /> */}
                    {/* Añadir otras rutas para secciones dentro del Dashboard */}
                </Route>

                {/* Redirigir cualquier ruta no encontrada al login */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
}

export default App;
