import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, List, ListItem, ListItemText, Select, MenuItem, InputLabel, FormControl } from '@mui/material';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import axios from 'axios';

const CrearProyecto = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [responsables, setResponsables] = useState([]); // Estado para responsables
  const [progresos, setProgresos] = useState([]); // Estado para progresos

  // Estados para los campos del formulario
  const [nombre, setNombre] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFinalizacion, setFechaFinalizacion] = useState('');
  const [estado, setEstado] = useState('En progreso');
  const [presupuesto, setPresupuesto] = useState('');
  const [idResponsable, setIdResponsable] = useState('');

  // Obtener los responsables desde la API al cargar el componente
  useEffect(() => {
    const fetchResponsables = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/responsables'); // Cambia esta URL según la API
        setResponsables(response.data); // Suponiendo que la respuesta contiene un array de responsables
      } catch (error) {
        setShowAlert({
          severity: 'error',
          message: 'No se pudieron cargar los responsables.',
        });
      }
    };

    fetchResponsables();
  }, []);

  // Obtener los progresos (Esto puede ser otra llamada API, o un estado de ejemplo para pruebas)
  useEffect(() => {
    setProgresos([
      { id: 1, progreso: 'Inicio del Proyecto' },
      { id: 2, progreso: 'Fase de planificación' },
      { id: 3, progreso: 'Ejecución en progreso' },
      { id: 4, progreso: 'Proyecto completado' },
    ]);
  }, []);

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples
    if (!nombre || !descripcion || !fechaInicio || !fechaFinalizacion || !estado || !presupuesto || !idResponsable) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const proyectoData = {
      nombre,
      descripcion,
      fecha_inicio: fechaInicio,
      fecha_finalizacion: fechaFinalizacion,
      estado,
      presupuesto,
      id_responsable: idResponsable
    };

    try {
      const response = await axios.post('http://localhost:5000/api/proyectos', proyectoData);

      if (response.data.msg === 'Proyecto creado correctamente') {
        setShowAlert({
          severity: 'success',
          message: 'Proyecto creado correctamente.',
        });

        // Limpiar los campos del formulario después de la inserción
        setNombre('');
        setDescripcion('');
        setFechaInicio('');
        setFechaFinalizacion('');
        setEstado('En progreso');
        setPresupuesto('');
        setIdResponsable('');
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el proyecto.',
        });
      }
    } catch (error) {
      setShowAlert({
        severity: 'error',
        message: 'Hubo un error al procesar tu solicitud. Inténtalo de nuevo.',
      });
    }
  };

  return (
    <Box sx={{ padding: 5 }}>
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000} // La alerta se cierra después de 2 segundos
          sx={{ mb: 4 }} // Agregar margen inferior a la alerta
        />
      )}

      <Typography variant="h4" gutterBottom>
        Crear Proyecto
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Campo para Nombre del Proyecto */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre del Proyecto"
              value={nombre}
              onChange={(e) => setNombre(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Descripción */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción"
              value={descripcion}
              onChange={(e) => setDescripcion(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Fecha de Inicio */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha de Inicio"
              type="date"
              value={fechaInicio}
              onChange={(e) => setFechaInicio(e.target.value)}
              required
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          {/* Campo para Fecha de Finalización */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha de Finalización"
              type="date"
              value={fechaFinalizacion}
              onChange={(e) => setFechaFinalizacion(e.target.value)}
              required
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          {/* Campo para Estado */}
          <Grid item xs={12}>
            <TextField
              fullWidth
              label="Estado"
              value={estado}
              onChange={(e) => setEstado(e.target.value)}
              required
            />
          </Grid>

          {/* Campo para Presupuesto */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Presupuesto"
              value={presupuesto}
              onChange={(e) => setPresupuesto(e.target.value)}
              required
              type="number"
            />
          </Grid>

          {/* Campo para Responsable */}
          <Grid item xs={12} sm={6}>
            <FormControl fullWidth>
              <InputLabel>Responsable</InputLabel>
              <Select
                value={idResponsable}
                onChange={(e) => setIdResponsable(e.target.value)}
                label="Responsable"
                required
              >
                {responsables.map((responsable) => (
                  <MenuItem key={responsable.id} value={responsable.id}>
                    {responsable.nombre}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>

          {/* Botón para enviar el formulario */}
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Crear Proyecto
            </Button>
          </Grid>
        </Grid>
      </form>

      {/* Mostrar los progresos en una lista */}
      <Typography variant="h6" gutterBottom sx={{ mt: 4 }}>
        Progresos del Proyecto
      </Typography>
      <List>
        {progresos.map((progreso) => (
          <ListItem key={progreso.id}>
            <ListItemText primary={progreso.progreso} />
          </ListItem>
        ))}
      </List>
    </Box>
  );
};

export default CrearProyecto;
