import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend
} from 'chart.js';
import { jwtDecode } from 'jwt-decode';

ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardChartsEstados = () => {
    const [proyectos, setProyectos] = useState({
        labels: ["En progreso", "Finalizado", "Suspendido"],
        datasets: [],
        estadoProyectos: {
            enProgreso: 0,
            finalizados: 0,
            suspendidos: 0
        }
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [startDate, setStartDate] = useState('');  // Estado para la fecha de inicio
    const [endDate, setEndDate] = useState('');      // Estado para la fecha de fin
    const [noData, setNoData] = useState(false);    // Nuevo estado para manejar la falta de datos

    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        try {
            const decoded = jwtDecode(token);
            const id = decoded.user?.id; // Verifica que `user` exista en el token.

            const config = {
                headers: {
                    'x-auth-token': token, 
                },
                params: {} // Añadimos parámetros de fecha para la solicitud
            };

            // Solo añadimos fechas a la solicitud si están definidas
            if (startDate) config.params.startDate = startDate;
            if (endDate) config.params.endDate = endDate;

            const response = await axios.get(
                `http://localhost:5000/api/grafica/EstadoProyectos/${id}`,
                config
            );

            // Verifica la respuesta de la API
            console.log('Datos recibidos de la API:', response.data);

            const { estadoProyectos } = response.data;

            if (!estadoProyectos || 
                (estadoProyectos.enProgreso === 0 && estadoProyectos.finalizados === 0 && estadoProyectos.suspendidos === 0)) {
                setNoData(true); // Si no hay proyectos, cambiamos el estado `noData`
                setError(null);   // Reseteamos cualquier error previo
                return;
            }

            setProyectos({
                labels: ["En progreso", "Finalizado", "Suspendido"],
                datasets: [
                    {
                        label: "Cantidad de Proyectos",
                        data: [
                            estadoProyectos.enProgreso,
                            estadoProyectos.finalizados,
                            estadoProyectos.suspendidos
                        ],
                        backgroundColor: ["blue", "green", "orange"], // Colores para los proyectos
                    }
                ],
                estadoProyectos,
            });
        } catch (err) {
            console.error('Error al obtener los proyectos:', err);
            const errorMsg = err.response?.data?.msg || 'Error desconocido';
            setError(`No se pudieron cargar los datos de los proyectos: ${errorMsg}`);
        } finally {
            setLoading(false);
        }
    }, [isAuthenticated, token, startDate, endDate]);

    useEffect(() => {
        if (!isAuthenticated) return;
        fetchProyectos();
    }, [isAuthenticated, fetchProyectos]);

    const handleDateChange = () => {
        setLoading(true); // Inicia la carga cuando se cambia el rango de fechas
        setNoData(false); // Resetear el estado de `noData` antes de cargar los datos
        fetchProyectos(); // Vuelve a hacer la solicitud con las nuevas fechas
    };

    if (loading) return <div>Cargando datos...</div>;

    if (error) {
        return <div>{error}</div>;
    }

    // Si no hay proyectos para mostrar
    if (noData) {
        return (
            <div style={{ textAlign: 'center', fontSize: '18px', color: '#888' }}>
                <p>No se encontraron proyectos para este rango de fechas. Intenta con otro período.</p>
            </div>
        );
    }

    // Verifica si los datos están correctamente estructurados antes de renderizar
    if (!proyectos.datasets || proyectos.datasets.length === 0) {
        return <div>No se encontraron proyectos asignados.</div>;
    }

    return (
        <div>
            <h2 style={{ textAlign: 'center' }}>Estado de los Proyectos</h2>

            {/* Filtros de fecha */}
            <div style={{ marginBottom: '20px', textAlign: 'center' }}>
                <label>Fecha de inicio: </label>
                <input
                    type="date"
                    value={startDate}
                    onChange={(e) => setStartDate(e.target.value)}
                />
                <label style={{ marginLeft: '10px' }}>Fecha de fin: </label>
                <input
                    type="date"
                    value={endDate}
                    onChange={(e) => setEndDate(e.target.value)}
                />
                <button onClick={handleDateChange} style={{ marginLeft: '10px' }}>
                    Filtrar
                </button>
            </div>

            <Pie
                data={{
                    labels: proyectos.labels,
                    datasets: proyectos.datasets,
                }}
                options={{
                    responsive: true,
                    plugins: {
                        legend: {
                            position: 'top',
                        },
                        tooltip: {
                            callbacks: {
                                label: (tooltipItem) => {
                                    const datasetIndex = tooltipItem.datasetIndex;
                                    const datasetLabel = proyectos.datasets[datasetIndex]?.label || 'Desconocido';
                                    const state = proyectos.labels[tooltipItem.dataIndex];
                                    const value = tooltipItem.raw;
                                    return `${datasetLabel}: ${value} Proyectos (${state})`;
                                },
                            },
                        },
                    },
                }}
            />
        </div>
    );
};

export default DashboardChartsEstados;
