import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { useSelector } from 'react-redux'; // Si estás usando Redux para manejar el estado

const DashboardLayoutNavigationLinks = () => {
  const [menuItems, setMenuItems] = useState([]);
  const [error, setError] = useState(null);
  const username = useSelector((state) => state.auth.username); // Asegúrate de tener el username en tu estado

  useEffect(() => {
    const fetchMenu = async () => {
      try {
        const response = await axios.get(`/api/menu/${username}`);
        setMenuItems(response.data); // Asigna los items del menú
      } catch (err) {
        setError(err);
        console.error('Error fetching menu:', err);
      }
    };

    fetchMenu();
  }, [username]); // Dependiendo de si el username cambia

  // Renderiza los elementos del menú
  if (error) {
    return <div>Error al cargar el menú: {error.message}</div>;
  }

  return (
    <div>
      {menuItems.map(item => (
        <div key={item.MenuID}>{item.Title}</div> // Asegúrate de que el nombre de las propiedades sea correcto
      ))}
    </div>
  );
};

export default DashboardLayoutNavigationLinks;
