import React, { useState, useEffect, useRef } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [nombre_hito, setNombreHito] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [hitos, setHitos] = useState([]); // Inicializar como array vacío
  const [resultadoEsperado, setResultadoEsperado] = useState('');
  const [pruebaRealizada, setPruebaRealizada] = useState('');
  const [criterioAceptacion, setCriterioAceptacion] = useState('');
  const [estadoPrueba, setEstadoPrueba] = useState('');
  const [estadonuevoHito, setEstadonuevoHito] = useState('');
  const [imagenPrueba, setImagenPrueba] = useState(null); // Estado para el archivo de imagen
  const [hitoSeleccionado, setHitoSeleccionado] = useState('');  // Valor vacío como predeterminado
  const [descripcionHito, setDescripcionHito] = useState(''); // Descripción del hito
  const [estadoActualHito, setEstadoActualHito] = useState(''); // Descripción del hito
  const [formularioHabilitado, setFormularioHabilitado] = useState(false); // Estado para habilitar solo los campos debajo del primer botón
  const [mostrarTabla, setMostrarTabla] = useState(false); // Estado para controlar la visibilidad de la tabla
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener el ID del proyecto desde la URL

  const alertRef = useRef(null); // Crear una referencia para la alerta

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    }
  }, [navigate]);

  // Manejar la subida de imágenes
  const handleImagenChange = (e) => {
    const file = e.target.files[0]; // Obtén el archivo seleccionado
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        setImagenPrueba(reader.result); // Guarda la cadena Base64 en el estado
      };
      reader.onerror = (error) => {
        console.error("Error al convertir la imagen a Base64:", error);
      };
      reader.readAsDataURL(file); // Convierte el archivo a Base64
    }
  };

  // Obtener los datos del proyecto
  useEffect(() => {
    const fetchProyecto = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/pruebas/hitosporPruebas/${id}`);
        const proyecto = response.data;

        setNombreHito(proyecto.nombre_hito);
        setDescripcion(proyecto.descripcion);

        // Obtener los hitos del proyecto
        setHitos(proyecto.hitos || []); // Si 'hitos' no existe, asigna un array vacío
      } catch (error) {
        console.error('Error al obtener el proyecto:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar los datos del proyecto.',
        });
      }
    };

    if (id) {
      fetchProyecto();
    }
  }, [id]);

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!hitoSeleccionado || !resultadoEsperado || !pruebaRealizada || !estadoPrueba || !criterioAceptacion) {
      setShowAlert({
 severity: 'error',
        message: 'Todos los campos del plan de prueba son obligatorios.',
      });
      return;
    }

    const planDePrueba = {
      hitoSeleccionado,
      estadonuevoHito,
      resultadoEsperado,
      pruebaRealizada,
      estadoPrueba,
      criterioAceptacion,
      imagenPrueba, // Aquí se envía como Base64
    };

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token,
          'Content-Type': 'application/json', // Asegúrate de usar JSON
        },
      };

      const response = await axios.post(
        `http://localhost:5000/api/pruebas/registerPrueba/${id}`,
        planDePrueba,
        config
      );

      if (response.data.msg) {
        setShowAlert({ severity: 'success', message: response.data.msg });
        setTimeout(() => navigate('/proyectos/tareas'), 2000);
      }
    } catch (error) {
      console.error('Error al crear el plan de prueba:', error.response || error);
      if (error.response && error.response.data && error.response.data.errors) {
        const errorMessages = error.response.data.errors.map(err => err.msg).join(', ');
        setShowAlert({
          severity: 'error',
          message: errorMessages,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el plan de prueba. Inténtalo de nuevo.',
        });
      }
    } finally {
      // Desplazar la vista hacia la alerta
      if (alertRef.current) {
        alertRef.current.scrollIntoView({ behavior: 'smooth' });
      }
    }
  };

  // Habilitar solo los campos debajo del primer botón
  const handleEnableForm = () => {
    setFormularioHabilitado(true);
  };

  // Función para mostrar/ocultar la tabla
  const handleVerCasos = () => {
    setMostrarTabla(!mostrarTabla);
  };

  return (
    <Box sx={{ padding: 5 }}>
      <div ref={alertRef}>
        {showAlert && (
          <AlertMessage
            severity={showAlert.severity}
            message={showAlert.message}
            autoHideDuration={2000}
            sx={{ mb: 4 }}
          />
        )}
      </div>

      <Typography variant="h4" gutterBottom align="center">
        Test Plan
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2} mt={3}>
          <Grid item xs={12} sm={12}>
            <InputLabel>Selecciona un Hito</InputLabel>
            <Select
              fullWidth
              value={hitoSeleccionado}
              onChange={(e) => setHitoSeleccionado(e.target.value)}
              displayEmpty // Para mostrar el placeholder aunque no se haya seleccionado nada
              required
            >
              <MenuItem value="" disabled>Selecciona un hito...</MenuItem> {/* Este es el placeholder */}
              {hitos.map((hito) => (
                <MenuItem key={hito.id_hito} value={hito.id_hito}>
                  {hito.nombre_hito}
                </MenuItem>
              ))}
            </Select>
          </Grid>

          <Grid item xs={12} sm={12}>
            <TextField
              fullWidth
              label="Descripción del Plan de Prueba"
              value={descripcionHito}
              disabled={true}
              variant="outlined"
            />
          </Grid>

          <Grid item xs={12}>
            <Button variant="contained" fullWidth onClick={handleEnableForm}>
              Habilitar Formulario
            </Button>
          </Grid>

          <Grid item xs={12}>
            <Button variant="contained" fullWidth onClick={handleVerCasos}>
              {mostrarTabla ? 'Ocultar Casos' : 'Ver Casos'}
            </Button>
          </Grid>
        </Grid>
      </form>

      {/* Tabla de Casos */}
      {mostrarTabla && (
        <TableContainer component={Paper} sx={{ marginTop: 3 }}>
          <Table>
            <TableHead>
              <TableRow>
                <TableCell>ID</TableCell>
                <TableCell>Nombre del Hito</TableCell>
                <TableCell>Descripción</TableCell>
                <TableCell>Estado</TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              { hitos.map((hito) => (
                <TableRow key={hito.id_hito}>
                  <TableCell>{hito.id_hito}</TableCell>
                  <TableCell>{hito.nombre_hito}</TableCell>
                  <TableCell>{hito.descripcion}</TableCell>
                  <TableCell>{hito.estado}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      )}
    </Box>
  );
};

export default CrearPlanDePrueba;
