import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import DashboardLayoutNavigationLinks from './components/DashboardLayoutNavigationLinks';
import Usuario from './pages/Seguridad/usuario';
import Permisos from './pages/Seguridad/permisos'; // Asegúrate de que el archivo existe
import ProtectedRoute from './components/ProtectedRoute';

const App = () => {
    return (
        <Router>
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route 
                    path="/dashboard/*" 
                    element={
                        <ProtectedRoute>
                            <DashboardLayoutNavigationLinks />
                        </ProtectedRoute>
                    } 
                />
                <Route path="/dashboard/Usuarios" element={<ProtectedRoute><Usuario /></ProtectedRoute>} />
                <Route path="/dashboard/Permisos" element={<ProtectedRoute><Permisos /></ProtectedRoute>} />
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
