// src/components/Dashboard.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Dashboard = ({ usuarioLogueado }) => {
    const [menuItems, setMenuItems] = useState([]);

    useEffect(() => {
        const fetchMenu = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/navigation/menu/${usuarioLogueado}`);
                setMenuItems(response.data);
            } catch (error) {
                console.error("Error al obtener el menú:", error);
            }
        };

        if (usuarioLogueado) {
            fetchMenu();
        }
    }, [usuarioLogueado]);

    return (
        <div>
            <h1>Dashboard</h1>
            <nav>
                <ul>
                    {menuItems.map((item) => (
                        <li key={item.MenuID}>
                            <a href={`/${item.MenuURL}`}>
                                <i className={`icon-${item.MenuIcon}`}></i> {item.DisplayName}
                            </a>
                        </li>
                    ))}
                </ul>
            </nav>
        </div>
    );
};

export default Dashboard;
