// src/App.js
import React from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './pages/Login';
import DashboardLayoutNavigationLinks from './components/DashboardLayoutNavigationLinks';
import UsuarioForm from './pages/Seguridad/usuario'; // Importa el formulario de usuario

const App = () => {
    const isAuthenticated = Boolean(localStorage.getItem('token')); // Verifica si hay un token
    console.log('Is Authenticated:', isAuthenticated); // Añade esto para depuración

    return (
        <Router>
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/dashboard" element={isAuthenticated ? <DashboardLayoutNavigationLinks /> : <Navigate to="/login" />} />
                <Route path="/dashboard" element={isAuthenticated ? <DashboardLayoutNavigationLinks /> : <Navigate to="/login" />} />

                <Route path="/dashboard/Usuarios" element={<UsuarioForm />} /> {/* Asegúrate de que esta ruta esté aquí */}
                {/* Redirige cualquier otra ruta al login */}
                <Route path="*" element={<Navigate to="/login" />} />
            </Routes>
        </Router>
    );
};

export default App;
