import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel, FormControl, CircularProgress } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar el componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar la función isAuthenticated

const CrearHito = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado de la alerta
  const [nombreProyecto, setNombreProyecto] = useState(''); // Nombre del proyecto relacionado
  const [nombreHito, setNombreHito] = useState(''); // Nombre del hito
  const [descripcionHito, setDescripcionHito] = useState(''); // Descripción del hito
  const [fechaInicio, setFechaInicio] = useState(''); // Fecha de inicio del hito
  const [fechaFin, setFechaFin] = useState(''); // Fecha de fin del hito
  const [estado, setEstado] = useState(''); // Estado del hito
  const navigate = useNavigate();
  const { id_proyecto } = useParams(); // Obtener el ID del proyecto desde la URL

  // Verificar si el usuario está autenticado al cargar el componente
  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige al login si no está autenticado
    }
  }, [navigate]);

  // Obtener el proyecto relacionado para mostrar el nombre
  useEffect(() => {
    const fetchProyecto = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/proyectos/proyecto/${id_proyecto}`);
        setNombreProyecto(response.data.nombre);
      } catch (error) {
        console.error('Error al obtener el proyecto:', error);
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al cargar el nombre del proyecto.',
        });
      }
    };

    if (id_proyecto) {
      fetchProyecto();
    }
  }, [id_proyecto]);

  // Función para manejar el envío del formulario
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validaciones simples
    if (!nombreHito || !descripcionHito || !fechaInicio || !fechaFin || !estado) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    const nuevoHito = {
      id_proyecto,
      nombreHito,
      descripcionHito,
      fechaInicio,
      fechaFin,
      estado,
    };

    try {
      const token = localStorage.getItem('token');

      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const config = {
        headers: {
          'x-auth-token': token,
        },
      };

      const response = await axios.post('http://localhost:5000/api/hitos/crear', nuevoHito, config);

      if (response.data.msg === 'Hito creado exitosamente') {
        setShowAlert({
          severity: 'success',
          message: response.data.msg,
        });

        // Redirigir a la lista de hitos después de 2 segundos
        setTimeout(() => {
          navigate(`/gestiones/proyectos/hitos/${id_proyecto}`);
        }, 2000);
      }
    } catch (error) {
      console.error('Error al crear el hito:', error.response || error);

      if (error.response && error.response.data && error.response.data.msg) {
        setShowAlert({
          severity: 'error',
          message: error.response.data.msg,
        });
      } else {
        setShowAlert({
          severity: 'error',
          message: 'Hubo un error al crear el hito. Inténtalo de nuevo.',
        });
      }
    }
  };

  return (
    <Box sx={{ padding: 5, maxWidth: 1000, margin: 'auto' }}>
      {/* Mostrar la alerta si hay alguna */}
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}

      <Typography variant="h4" gutterBottom align="center">
        Crear Hito
      </Typography>

      {/* Mostrar el nombre del proyecto */}
      <Typography variant="h6" gutterBottom>
        Proyecto: {nombreProyecto || 'Cargando...'}
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          {/* Campo para Nombre del Hito */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Nombre del Hito"
              value={nombreHito}
              onChange={(e) => setNombreHito(e.target.value)}
              required
              variant="outlined"
            />
          </Grid>

          {/* Campo para Descripción */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Descripción del Hito"
              value={descripcionHito}
              onChange={(e) => setDescripcionHito(e.target.value)}
              required
              variant="outlined"
            />
          </Grid>

          {/* Campo para Fecha de Inicio */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha de Inicio"
              type="date"
              value={fechaInicio}
              onChange={(e) => setFechaInicio(e.target.value)}
              required
              variant="outlined"
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          {/* Campo para Fecha de Fin */}
          <Grid item xs={12} sm={6}>
            <TextField
              fullWidth
              label="Fecha de Fin"
              type="date"
              value={fechaFin}
              onChange={(e) => setFechaFin(e.target.value)}
              required
              variant="outlined"
              InputLabelProps={{
                shrink: true,
              }}
            />
          </Grid>

          {/* Campo para Estado del Hito */}
          <Grid item xs={12}>
            <FormControl fullWidth required variant="outlined">
              <InputLabel>Estado del Hito</InputLabel>
              <Select
                value={estado}
                onChange={(e) => setEstado(e.target.value)}
                label="Estado del Hito"
              >
                <MenuItem value="En progreso">En Progreso</MenuItem>
                <MenuItem value="Finalizado">Finalizado</MenuItem>
                <MenuItem value="Pendiente">Pendiente</MenuItem>
              </Select>
            </FormControl>
          </Grid>

          {/* Botón de Enviar */}
          <Grid item xs={12}>
            <Button type="submit" variant="contained" color="primary" fullWidth>
              Crear Hito
            </Button>
          </Grid>
        </Grid>
      </form>
    </Box>
  );
};

export default CrearHito;
