import React, { useEffect, useState, useCallback } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import {
    Chart as ChartJS,
    ArcElement,
    Tooltip,
    Legend
} from 'chart.js';
import {jwtDecode} from 'jwt-decode'; // Corrección: quitar el uso de {}.

ChartJS.register(ArcElement, Tooltip, Legend);

const DashboardCharts = () => {
    const [proyectos, setProyectos] = useState({
        labels: [],
        datasets: [],
        vencidos: 0,
        noVencidos: 0,
        vencidosNombres: [],
        noVencidosNombres: []
    });
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const token = localStorage.getItem('token');
const isAuthenticated = Boolean(token);
if (!isAuthenticated) {
    setError('El token ha expirado o no está presente.');
    return;
}


    const fetchProyectos = useCallback(async () => {
        if (!isAuthenticated) return;

        try {
            const decoded = jwtDecode(token);
            const id = decoded.user?.id; // Validar que `user` exista.

            const config = {
                headers: {
                    Authorization: `Bearer ${token}`, // Estándar para autorización.
                },
            };

            const response = await axios.get(
                `http://localhost:5000/api/grafica/VencimientoProyecto/${id}`,
                config
            );

            const {
                labels,
                vencidos,
                noVencidos,
                vencidosNombres,
                noVencidosNombres,
            } = response.data;

            setProyectos({
                labels,
                datasets: [
                    {
                        data: [vencidos, noVencidos],
                        backgroundColor: ['#FF6384', '#36A2EB'],
                    },
                ],
                vencidos,
                noVencidos,
                vencidosNombres,
                noVencidosNombres,
            });
        } catch (err) {
            console.error('Error al obtener los proyectos:', err);
            setError('No se pudieron cargar los datos de los proyectos.');
        } finally {
            setLoading(false);
        }
    }, [isAuthenticated, token]);

    useEffect(() => {
        if (isAuthenticated) {
            fetchProyectos();
        }
    }, [isAuthenticated, fetchProyectos]);

    if (loading) return <div>Cargando datos...</div>;

    if (error) {
        return <div>{error}</div>;
    }

    if (!proyectos.labels.length || !proyectos.datasets.length) {
        return <div>No se encontraron proyectos asignados.</div>;
    }

    return (
        <div>
            <h2>Proyectos Próximos a Vencer</h2>
            <Pie
                data={{
                    labels: proyectos.labels,
                    datasets: proyectos.datasets,
                }}
                options={{
                    responsive: true,
                    plugins: {
                        legend: {
                            position: 'top',
                        },
                        tooltip: {
                            callbacks: {
                                label: (tooltipItem) => {
                                    const index = tooltipItem.dataIndex;
                                    const projectNames =
                                        index === 0
                                            ? proyectos.vencidosNombres
                                            : proyectos.noVencidosNombres;
                                    return `${tooltipItem.raw} Proyectos: ${projectNames.join(', ')}`;
                                },
                            },
                        },
                    },
                }}
            />
        </div>
    );
};

export default DashboardCharts;
