import * as React from 'react';
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import GroupIcon from '@mui/icons-material/Group';
import PeopleIcon from '@mui/icons-material/People';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { useNavigate, useLocation } from 'react-router-dom';
import axios from 'axios'; 
import { jwtDecode } from 'jwt-decode';

const demoTheme = createTheme({
  palette: {
    mode: 'light',
  },
});

function DemoPageContent({ pathname }) {
  return (
    <Box
      sx={{
        py: 4,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        textAlign: 'center',
      }}
    >
      <Typography>Dashboard content for {pathname}</Typography>
    </Box>
  );
}

export default function DashboardLayoutNavigationLinks() {
  const navigate = useNavigate();
  const location = useLocation();
  const pathname = location.pathname;
  const [menuItems, setMenuItems] = React.useState([]);

  const fetchMenus = async () => {
    const token = localStorage.getItem('token');
    if (!token) return;

    const decoded = jwtDecode(token);
    const username = decoded.user.username;

    try {
      const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
      setMenuItems(response.data);
    } catch (error) {
      console.error('Error fetching menus:', error);
    }
  };

  const getIcon = (iconName) => {
    switch (iconName) {
      case 'Group':
        return <GroupIcon />;
      case 'People':
        return <PeopleIcon />;
      case 'Description':
        return <DescriptionIcon />;
      default:
        return <DescriptionIcon />; // Ícono predeterminado
    }
  };

  React.useEffect(() => {
    fetchMenus();
  }, []);

  // Estructura del menú con dropdown
  const navigation = menuItems.map(item => {
    if (item.ParentMenuID === 0) { // Solo el menú principal
      return {
        segment: item.DisplayName,
        title: item.DisplayName,
        icon: getIcon(item.MenuIcon), // Usa el nombre del ícono de la base de datos
        children: menuItems.filter(subItem => subItem.ParentMenuID === item.MenuID),
      };
    }
    return null;
  }).filter(Boolean);

  return (
    <AppProvider
      navigation={navigation}
      router={{
        pathname,
        navigate: (path) => navigate(path),
      }}
      theme={demoTheme}
    >
      <DashboardLayout>
        <DemoPageContent pathname={pathname} />
      </DashboardLayout>
    </AppProvider>
  );
}
