import React, { useEffect, useState, useCallback } from 'react';
import { createTheme } from '@mui/material/styles';
import DescriptionIcon from '@mui/icons-material/Description';
import PersonIcon from '@mui/icons-material/Person';
import LockIcon from '@mui/icons-material/Lock';
import SecurityIcon from '@mui/icons-material/Security';
import { AppProvider } from '@toolpad/core/AppProvider';
import { DashboardLayout } from '@toolpad/core/DashboardLayout';
import { Routes, Route, useNavigate } from 'react-router-dom';
import axios from 'axios';
import { jwtDecode } from 'jwt-decode';
import Button from '@mui/material/Button'; // Importa el componente Button de MUI
import Usuario from '../pages/Seguridad/usuario';
import Roles from '../pages/Seguridad/Roles';
import CrearUsuario from '../pages/Seguridad/CrearUsuario'; 
import EditarUsuario from '../pages/Seguridad/EditarUsuario';
import CrearRoles from '../pages/Seguridad/CrearRoles'; 
import EditarRol from '../pages/Seguridad/EditarRol'; 
import CrearPermisos from '../pages/Seguridad/CrearPermisos'; 
import Proyectos from '../pages/Gestiones/Proyectos';
import CrearProyecto from '../pages/Gestiones/CrearProyectos';
import EditarProyecto from '../pages/Gestiones/EditarProyecto'; 
import CrearHitos from '../pages/Gestiones/CrearHitos'; 
import EditarHito from '../pages/Gestiones/EditarHito'; 
import Tareas from '../pages/Proyectos/Tareas'; 
import EditarTarea from '../pages/Proyectos/EditarTarea'; 
import CrearPruebas from '../pages/Proyectos/PlanPruebas'; 
import VerPruebasHito from '../pages/Proyectos/Hitos'; 
import EditarPrueba from '../pages/Proyectos/EditarPrueba'; 

const iconMap = {
    Seguridad: <SecurityIcon />,
    Roles: <PersonIcon />,
    Usuarios: <DescriptionIcon />,
    Permisos: <LockIcon />,
};

const demoTheme = createTheme({
    palette: {
        mode: 'light',
    },
});

export default function DashboardLayoutNavigationLinks() {
    const navigate = useNavigate();
    const [menuItems, setMenuItems] = useState([]);
    const token = localStorage.getItem('token');
    const isAuthenticated = Boolean(token);

    const fetchMenus = useCallback(async () => {
        if (!token) return;

        const decoded = jwtDecode(token);
        const username = decoded.user.username;

        try {
            const response = await axios.get(`http://localhost:5000/api/navigation/menu/${username}`);
            console.log('Menú obtenido:', response.data);
            setMenuItems(response.data);
        } catch (error) {
            console.error('Error al obtener los menús:', error);
        }
    }, [token]);

    useEffect(() => {
        if (!isAuthenticated) {
            console.log('Usuario no autenticado, redirigiendo al login.');
            navigate('/login');
        } else {
            fetchMenus();
        }
    }, [isAuthenticated, fetchMenus, navigate]);

    const handleMenuClick = (url) => {
        if (url) {
            console.log('Navegando a:', url);
            navigate(`/${url}`);
        }
    };

    const handleLogout = () => {
        localStorage.removeItem('token'); // Elimina el token del almacenamiento local
        navigate('/login'); // Redirige al usuario a la página de inicio de sesión
    };

    const navigation = menuItems.map((item) => {
        if (item.ParentMenuID === 0 && item.VisibleUsuario === 1) {
            return {
                segment: item.MenuURL,
                title: item.DisplayName,
                icon: iconMap[item.DisplayName] || <DescriptionIcon />,
                children: menuItems
                    .filter((subItem) => subItem.ParentMenuID === item.MenuID)
                    .map((subItem) => ({
                        segment: subItem.MenuURL,
                        title: subItem.DisplayName,
                        icon: iconMap[subItem.DisplayName] || <DescriptionIcon />,
                        onClick: () => handleMenuClick(subItem.MenuURL),
                    })),
                onClick: () => handleMenuClick(item.MenuURL),
            };
        }
        return null;
    }).filter(Boolean);

    return (
        <AppProvider navigation={navigation} theme={demoTheme}>
            <Button variant="contained" color="secondary" onClick={handleLogout}>
                    Cerrar Sesión
                </Button>
            <DashboardLayout>
                
                <Routes>
                    <Route path="seguridad/usuarios" element={<Usuario />} />
                    <Route path="seguridad/crear-usuario" element={<CrearUsuario />} />
                    <Route path="seguridad/editar-usuario/:id" element={<EditarUsuario />} />
                    <Route path="seguridad/roles" element={<Roles />} />
                    <Route path="seguridad/crear-roles" element={<CrearRoles />} />
                    <Route path="seguridad/editar-roles/:id" element={<EditarRol />} />
                    <Route path="seguridad/crear-permisos/:id" element={<CrearPermisos />} />
                    <Route path="gestiones/proyectos" element={<Proyectos />} />
                    <Route path="gestiones/crear-proyectos" element={<CrearProyecto />} />
                    <Route path="gestiones/editar-proyectos/:id" element={<EditarProyecto />} />
                    <Route path="gestiones/crear-hitos/:id" element={<CrearHitos />} />
                    <Route path="gestiones/editar-hitos/:id" element={<EditarHito />} />
                    <Route path="proyectos/tareas" element={<Tareas />} />
                    <Route path="proyectos/editar-tarea/:id" element={<EditarTarea />} />
                    <Route path="proyectos/crear-pruebas/:id" element={<CrearPruebas />} />
                    <Route path="proyectos/hitos-pruebas/:id" element={<VerPruebasHito />} />
                    <Route path="proyectos/editar-plan/:id" element={<EditarPrueba />} />
                </Routes>
            </DashboardLayout>
        </AppProvider>
    );
}
