import React, { useState, useEffect } from 'react';
import { Box, TextField, Button, Grid, Typography, MenuItem, Select, InputLabel } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage'; // Importar componente de alerta
import { isAuthenticated } from '../../utils/auth'; // Importar autenticación

const CrearPlanDePrueba = () => {
  const [showAlert, setShowAlert] = useState(null); // Estado para alertas
  const [nombreProyecto, setNombreProyecto] = useState('');
  const [descripcion, setDescripcion] = useState('');
  const [fechaInicio, setFechaInicio] = useState('');
  const [fechaFin, setFechaFin] = useState('');
  const [presupuesto, setPresupuesto] = useState('');
  const [estado, setEstado] = useState('');
  const [responsable, setResponsable] = useState('');
  const [hitos, setHitos] = useState([]); // Lista de hitos
  const [resultadoEsperado, setResultadoEsperado] = useState('');
  const [pruebaRealizada, setPruebaRealizada] = useState('');
  const [criterioAceptacion, setCriterioAceptacion] = useState('');
  const [estadoPrueba, setEstadoPrueba] = useState('');
  const [imagenPrueba, setImagenPrueba] = useState(null); // Estado para imagen
  const [hitoSeleccionado, setHitoSeleccionado] = useState('');
  const [descripcionHito, setDescripcionHito] = useState('');
  const [formularioHabilitado, setFormularioHabilitado] = useState(false); // Habilitar campos
  const navigate = useNavigate();
  const { id } = useParams(); // Obtener ID del proyecto desde la URL

  useEffect(() => {
    if (!isAuthenticated()) {
      navigate('/login'); // Redirige si no está autenticado
    }
  }, [navigate]);

  useEffect(() => {
    const fetchProyecto = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/proyectos/proyecto/${id}`);
        const proyecto = response.data;

        setNombreProyecto(proyecto.nombre);
        setDescripcion(proyecto.descripcion);
        setFechaInicio(new Date(proyecto.fecha_inicio).toISOString().split('T')[0]);
        setFechaFin(new Date(proyecto.fecha_finalizacion).toISOString().split('T')[0]);
        setPresupuesto(proyecto.presupuesto);
        setEstado(proyecto.estado);
        setResponsable(proyecto.u_usuario);
        setHitos(proyecto.hitos || []);
      } catch (error) {
        console.error('Error al cargar el proyecto:', error);
        setShowAlert({
          severity: 'error',
          message: 'Error al cargar los datos del proyecto.',
        });
      }
    };

    if (id) {
      fetchProyecto();
    }
  }, [id]);

  const handleImagenChange = (e) => {
    setImagenPrueba(e.target.files[0]);
  };

  const handleHitoSeleccionado = (e) => {
    const hitoId = e.target.value;
    setHitoSeleccionado(hitoId);

    const hito = hitos.find((hito) => hito.id_hito === hitoId);
    setDescripcionHito(hito ? hito.descripcion : '');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!hitoSeleccionado || !resultadoEsperado || !pruebaRealizada || !estadoPrueba || !criterioAceptacion) {
      setShowAlert({
        severity: 'error',
        message: 'Todos los campos son obligatorios.',
      });
      return;
    }

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        setShowAlert({
          severity: 'error',
          message: 'No se encontró el token de autenticación.',
        });
        return;
      }

      const formData = new FormData();
      formData.append('resultadoEsperado', resultadoEsperado);
      formData.append('pruebaRealizada', pruebaRealizada);
      formData.append('estadoPrueba', estadoPrueba);
      formData.append('hitoSeleccionado', hitoSeleccionado);
      formData.append('criterioAceptacion', criterioAceptacion);
      formData.append('imagenPrueba', imagenPrueba); // Agregar la imagen al FormData

      const config = {
        headers: {
          'x-auth-token': token,
          'Content-Type': 'multipart/form-data', // Configurar el encabezado para FormData
        },
      };

      const response = await axios.post(`http://localhost:5000/api/proyectos/crearPlanDePrueba/${id}`, formData, config);

      setShowAlert({
        severity: 'success',
        message: response.data.msg,
      });

      setTimeout(() => {
        navigate('/proyectos/tareas');
      }, 2000);
    } catch (error) {
      console.error('Error al crear el plan de prueba:', error);
      setShowAlert({
        severity: 'error',
        message: 'Error al crear el plan de prueba.',
      });
    }
  };

  const handleEnableForm = () => {
    setFormularioHabilitado(true);
  };

  return (
    <Box sx={{ padding: 5 }}>
      {showAlert && (
        <AlertMessage
          severity={showAlert.severity}
          message={showAlert.message}
          autoHideDuration={2000}
          sx={{ mb: 4 }}
        />
      )}
      <Typography variant="h4" gutterBottom align="center">
        Crear Plan de Prueba
      </Typography>

      <form onSubmit={handleSubmit}>
        <Grid container spacing={2}>
          <Grid item xs={12} sm={3}>
            <TextField fullWidth label="Nombre del Proyecto" value={nombreProyecto} disabled variant="outlined" />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField fullWidth label="Descripción" value={descripcion} disabled variant="outlined" />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField fullWidth label="Fecha de Inicio" type="date" value={fechaInicio} disabled variant="outlined" />
          </Grid>

          <Grid item xs={12} sm={3}>
            <TextField fullWidth label="Fecha de Fin" type="date" value={fechaFin} disabled variant="outlined" />
          </Grid>

          <Grid item xs={12}>
            <Button variant="contained" fullWidth onClick={handleEnableForm}>
              Habilitar Formulario
            </Button>
          </Grid>
        </Grid>

        {formularioHabilitado && (
          <>
            <Grid container spacing={2} mt={3}>
              <Grid item xs={12}>
                <InputLabel>Selecciona un Hito</InputLabel>
                <Select fullWidth value={hitoSeleccionado} onChange={handleHitoSeleccionado} displayEmpty>
                  <MenuItem value="" disabled>
                    Selecciona un hito...
                  </MenuItem>
                  {hitos.map((hito) => (
                    <MenuItem key={hito.id_hito} value={hito.id_hito}>
                      {hito.nombre_hito}
                    </MenuItem>
                  ))}
                </Select>
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Descripción del Hito"
                  value={descripcionHito}
                  disabled
                  variant="outlined"
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Resultado Esperado"
                  value={resultadoEsperado}
                  onChange={(e) => setResultadoEsperado(e.target.value)}
                  variant="outlined"
                  required
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Prueba Realizada"
                  value={pruebaRealizada}
                  onChange={(e) => setPruebaRealizada(e.target.value)}
                  variant="outlined"
                  required
                />
              </Grid>

              <Grid item xs={12}>
                <TextField
                  fullWidth
                  label="Criterio de Aceptación"
                  value={criterioAceptacion}
                  onChange={(e) => setCriterioAceptacion(e.target.value)}
                  variant="outlined"
                  required
                />
              </Grid>

              <Grid item xs={12}>
                <InputLabel>Estado de la Prueba</InputLabel>
                <Select
                  fullWidth
                  value={estadoPrueba}
                  onChange={(e) => setEstadoPrueba(e.target.value)}
                  displayEmpty
                  required
                >
                  <MenuItem value="" disabled>
                    Selecciona un estado...
                  </MenuItem>
                  <MenuItem value="aprobada">Aprobada</MenuItem>
                  <MenuItem value="rechazada">Rechazada</MenuItem>
                </Select>
              </Grid>

              <Grid item xs={12}>
                <InputLabel>Subir Imagen de la Prueba</InputLabel>
                <TextField
                  type="file"
                  fullWidth
                  inputProps={{ accept: 'image/*' }}
                  onChange={handleImagenChange}
                />
              </Grid>

              <Grid item xs={12}>
                <Button variant="contained" type="submit" fullWidth>
                  Crear Plan de Prueba
                </Button>
              </Grid>
            </Grid>
          </>
        )}
      </form>
    </Box>
  );
};

export default CrearPlanDePrueba;
