import React, { useState, useEffect, useCallback } from 'react';
import { Box, TextField, Button, Grid, Typography, Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, MenuItem, Select, InputLabel, FormControl, CircularProgress } from '@mui/material';
import { useNavigate, useParams } from 'react-router-dom';
import axios from 'axios';
import AlertMessage from '../../components/AlertMessage';
import { isAuthenticated } from '../../utils/auth';

const CrearHito = () => {
    const { proyectoId } = useParams();
    const [showAlert, setShowAlert] = useState(null);
    const [titulo, setTitulo] = useState('');
    const [descripcion, setDescripcion] = useState('');
    const [fechaInicio, setFechaInicio] = useState('');
    const [fechaFin, setFechaFin] = useState('');
    const [estado, setEstado] = useState('Pendiente');
    const [proyecto, setProyecto] = useState(null);
    const [hitos, setHitos] = useState([]);
    const navigate = useNavigate();

    // Función general para realizar peticiones
    const makeRequest = async (method, url, data = {}) => {
        const token = localStorage.getItem('token');
        if (!token) {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el token de autenticación.',
            });
            return;
        }

        const config = { headers: { 'x-auth-token': token } };
        try {
            const response = await axios[method](url, data, config);
            return response.data;
        } catch (error) {
            console.error('Error:', error.response || error);
            setShowAlert({
                severity: 'error',
                message: error.response?.data?.msg || 'Hubo un error al realizar la solicitud.',
            });
            return null;
        }
    };

    // Obtener proyecto y hitos
    const fetchProyecto = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        if (data) {
            setProyecto(data);
        } else {
            setShowAlert({
                severity: 'error',
                message: 'No se encontró el proyecto.',
            });
        }
    }, [proyectoId]);

    const fetchHitos = useCallback(async () => {
        const data = await makeRequest('get', `http://localhost:5000/api/hitos/proyecto/${proyectoId}`);
        if (data) {
            setHitos(data);
        } else {
            setHitos([]);
        }
    }, [proyectoId]);

    useEffect(() => {
        if (!isAuthenticated()) {
            navigate('/login');
        } else {
            fetchProyecto();
            fetchHitos();
        }
    }, [navigate, fetchProyecto, fetchHitos]);

    // Manejo del formulario
    const handleSubmit = async (e) => {
        e.preventDefault();

        console.log('proyectoId en handleSubmit:', proyectoId); // Debugging

        if (!titulo || !descripcion || !fechaInicio || !fechaFin) {
            setShowAlert({
                severity: 'error',
                message: 'Todos los campos son obligatorios.',
            });
            return;
        }

        // Verifica que proyectoId tenga un valor válido
        if (!proyectoId) {
            setShowAlert({
                severity: 'error',
                message: 'ID del proyecto no encontrado.',
            });
            return;
        }

        // Crear el nuevo hito con el proyectoId incluido
        const newHito = { 
            titulo, 
            descripcion, 
            fechaInicio, 
            fechaFin, 
            estado,
            proyectoId // Agregar el proyectoId al cuerpo de la solicitud
        };

        // Realizar la solicitud POST
        const response = await makeRequest('post', 'http://localhost:5000/api/hitos/register', newHito);

        if (response && response.msg === 'Hito creado correctamente') {
            setShowAlert({
                severity: 'success',
                message: response.msg,
            });

            // Limpiar el formulario
            setTitulo('');
            setDescripcion('');
            setFechaInicio('');
            setFechaFin('');
            setEstado('Pendiente');
            fetchHitos(); // Actualizar la lista de hitos
        }
    };

    return (
        <Box>
            {showAlert && <AlertMessage severity={showAlert.severity} message={showAlert.message} />}
            <form onSubmit={handleSubmit}>
                <TextField
                    label="Título"
                    value={titulo}
                    onChange={(e) => setTitulo(e.target.value)}
                    required
                />
                <TextField
                    label="Descripción"
                    value={descripcion}
                    onChange={(e) => setDescripcion(e.target.value)}
                    required
                />
                <TextField
                    label="Fecha de Inicio"
                    type="date"
                    value={fechaInicio}
                    onChange={(e) => setFechaInicio(e.target.value)}
                    required
                />
                <TextField
                    label="Fecha de Fin"
                    type="date"
                    value={fechaFin}
                    onChange={(e) => setFechaFin(e.target.value)}
                    required
                />
                <FormControl>
                    <InputLabel>Estado</InputLabel>
                    <Select
                        value={estado}
                        onChange={(e) => setEstado(e.target.value)}
                    >
                        <MenuItem value="Pendiente">Pendiente</MenuItem>
                        <MenuItem value="Completado">Completado</MenuItem>
                    </Select>
                </FormControl>
                <Button type="submit">Crear Hito</Button>
            </form>
            <TableContainer component={Paper}>
                <Table>
                    <TableHead>
                        <TableRow>
                            <TableCell>Título</TableCell>
                            <TableCell>Descripción</TableCell>
                            <TableCell>Fecha de Inicio</TableCell>
                            <TableCell>Fecha de Fin</TableCell>
                            <TableCell>Estado</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {hitos.map((hito) => (
                            <TableRow key={hito.id}>
                                <TableCell>{hito.titulo}</TableCell>
                                <TableCell>{hito.descripcion}</TableCell>
                                <TableCell>{hito.fechaInicio}</TableCell>
                                <TableCell>{hito.fechaFin}</TableCell>
                                <TableCell>{hito.estado}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </Box>
    );
};

export default CrearHito;
