import React from 'react';
import Sidebar from './DashboardLayoutNavigationLinks'; // Asegúrate de que este sea tu componente de menú
import Box from '@mui/material/Box';
import Typography from '@mui/material/Typography';

const DashboardPageLayout = ({ navigation, children }) => {
    return (
        <Box sx={{ display: 'flex' }}>
            <Sidebar navigation={navigation} /> {/* Componente de menú lateral */}
            <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
                {children} {/* Aquí se renderiza el contenido de las rutas */}
            </Box>
            <DemoPageContent />
        </Box>
    );
};

function DemoPageContent() {
    return (
        <Box
            sx={{
                py: 35,
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'center',
                textAlign: 'center',
            }}
        >
            <Typography variant="h4">Bienvenido al Gestor de Control de Calidad!</Typography>
        </Box>
    );
}
export default DashboardPageLayout;
